css:
@namespace url(http://www.w3.org/1999/xhtml);

/*--------------------------------------*/
/*     C S S   b y   H i r u m a n      */
/*--------------------------------------*/

@-moz-document url-prefix(http://www.youtube.com){

/*Major Changes*/
body,input,#watch-video-container{background-color:#000!important;}
div,span,p{background-color:transparent!important;}
*{border-color:#444!important;color:#bbb!important;}
a,span.title{color:#555!important;}
a:hover,span.title:hover{color:#666!important;}
.video-time{background-color:#000!important;opacity:0.8!important;}
/*-------------*/

/*Watch video page*/
/*---Featured Video*/
.watch-ppv-vid{background-color:#222!important;}
/*---Alert*/
.yt-alert-announce,.yt-alert-info,.yt-alert-status,.yt-alert-content,.yt-alert-content strong{color:#000!important;background-color:#aaa!important;}
/*---Watch Info*/
#watch-info.expanded{-moz-box-shadow:0 0 9px #444!important;}
/*---Video Details*/
.watch-expander-head:hover{background-color:#222!important;}
/*---Views Details*/
.watch-stats-title-cell{background-color:#222!important;}
/*-----------------*/

/*Rate, Share, Embed buttons*/
.yt-uix-button-arrow{border-left-color:transparent!important;border-right-color:transparent!important;}
.yt-uix-button{border-color:#222!important;background:-moz-linear-gradient(center top , #222, #111) repeat scroll 0 0 #000!important;}
.yt-uix-button:active,.yt-uix-button-active{border-color:#555!important;background:-moz-linear-gradient(center top , #444, #222) repeat scroll 0 0 #000!important;}
/*--------------------------*/

/*Tooltip*/
.yt-uix-tooltip-tip-arrow{opacity:0.85!important;border-top-color:#bbb!important;border-left-color:transparent!important;border-right-color:transparent!important}
.yt-uix-tooltip-tip-content{-moz-box-shadow:0 0 6px #fff;color:#000!important;opacity:0.85!important;background-color:#bbb!important;}
/*-------*/

/*Related Videos, Next Search Result*/
.video-list-item-link:hover{background-color:#222!important;}
.yt-rounded-top{background-color:#222!important;}
.next-list-current{background-color:#333!important;}
/*----------------------------------*/

/*Comments*/
#watch-comments-actions{background-color:transparent!important;}
#watch-discussion li{border-color:transparent!important;}
/*--------*/
}
