@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix('http://wowger.de/site/index'),
url-prefix('http://wowger.de/site/content'),
url-prefix('http://wowger.de/site/wowstatus.php'),
url-prefix('http://wowger.de/site/comment'),
url-prefix('http://wowger.de/site/member'),
url-prefix('http://wowger.de/site/groups'),
url-prefix('http://wowger.de/site/poll'),
url-prefix('http://wowger.de/site/user_cp.php'),
url-prefix('http://wowger.de/site/report_user.php'),
url-prefix('http://wowger.de/site/user_gal'),
url-prefix('http://wowger.de/site/pm'),
url-prefix('http://wowger.de/site/search_user.php')
{
body {
  background-color: #000066 !important;
  font-family: arial,Verdana, Arial, Helvetica, sans-serif !important;
  background-attachment: scroll !important;
}
.nav {
   background-color: #0000FF !important;
}
.footer {
  background-color: #0000FF !important;
}
.back {
   background-color: #0000FF !important;
}
.back1 {
   background-color: #0000FF !important;
}
.leiste_unten {
  background-color: #0000FF !important;
}
.leiste_oben {
  background-color: #0000FF !important;
}
link {
   color: #000000 !important;
}
hover {
   color: #0066FF !important;
}
input{
  font-family: arial,Verdana, Arial, Helvetica, sans-serif !important;
  color: #000000 !important;
  background-color: #0066FF !important;
}
body,td,th {
   color: #000000 !important;
}
a:link {
   color: #000000 !important;
}
