css:
@-moz-document domain("news.yahoo.co.jp"), domain("headlines.yahoo.co.jp") {
    #contents {
        min-width: 950px;
        width: 100%;
    }

    #contents #yjnMain {
        min-width: 670px;
        max-width: unset;
        width: calc(100% - 10px);
    }

    @media screen and (max-width: 989px) {
        /* no sidebars when window is small */

        #contents #yjnSub{
            display: none;
        }
        #contents #yjnMain, .yj950-2 #main {
            min-width: 0px;
            width: 95%;
        }
        #contents, #wrapper{
            min-width: unset;
        }
    }
}
