css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url('http://wowger.de/site/'),
url-prefix('http://wowger.de/site/index'),
url-prefix('http://wowger.de/site/wowstatus.php'),
url-prefix('http://wowger.de/site/comment'),
url-prefix('http://wowger.de/site/content'),
url-prefix('http://wowger.de/site/member'),
url-prefix('http://wowger.de/site/groups'),
url-prefix('http://wowger.de/site/poll'),
url-prefix('http://wowger.de/site/user_cp.php'),
url-prefix('http://wowger.de/site/report_user.php'),
url-prefix('http://wowger.de/site/user_gal'),
url-prefix('http://wowger.de/site/pm'),
url-prefix('http://wowger.de/site/search_user.php'),
url-prefix('http://wowger.de/site/fan_gal')
{
html, body {
  background-color: #000000 !important;
  background-image: none !important;
  font-family: Franklin Gothic Medium,arial,Verdana, Arial, Helvetica, sans-serif !important;
}
.nav {
  background-color: #111111 !important;
}
.footer {
  background-color: #111111 !important;
}
.back {
   background-color: #111111 !important;
}
.back1 {
   background-color: #111111 !important;
}
.leiste_unten {
  background-color: #111111 !important;
}
.leiste_oben {
  background-color: #111111 !important;
}
input{
  font-family: arial,Verdana, Arial, Helvetica, sans-serif !important;
  color: #BCBCBC !important;
  background-color: #000000 !important;
}
body,td,th {
  color: #BCBCBC !important;
}
a:link {
   color: #696969 !important;
}
a:visited {
   color: #696969 !important;
}
a:hover {
   color: #CDC0B0 !important;
}
a:active {
   color: #696969 !important;
}
.header {
  background-image: url(http://img27.imageshack.us/img27/5200/headerwowger.jpg) !important;
}
.member{
  background-color: #111111 !important;
  border: 1px solid #111111 !important;
}
.member-right2{
  background-color: #111111 !important;
  border: 1px solid #111111 !important;
}
.member-right1{
  border: 1px solid #111111 !important;
}
.member-top{
  background-color: #111111 !important;
  border-bottom: 1px solid #111111 !important;
  border-top: 1px solid #111111 !important;
  border-left: 1px solid #111111 !important;
  border-right: 0px solid #111111 !important;
}
select{
  font-family: arial,Verdana, Arial, Helvetica, sans-serif !important;
  color: #696969 !important;
  background-color: #000000 !important;
}
textarea{
  font-family: arial,Verdana, Arial, Helvetica, sans-serif !important;
  color: #696969 !important;
  background-color: #000000 !important;
}
}
@-moz-document
url-prefix('http://wowger.de/site/content.php?action=deathcathedral')
{
body {
  height:300% !important;
  background-color: #000000 !important;
  font-family: Franklin Gothic Medium,arial,Verdana, Arial, Helvetica, sans-serif !important;
}
}
@-moz-document
url-prefix('http://wowger.de/site/content.php?action=gamingrules')
{
body {
  height:310% !important;
  background-color: #000000 !important;
  font-family: Franklin Gothic Medium,arial,Verdana, Arial, Helvetica, sans-serif !important;
}
}
