css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://boards.4chan.org/a/"), url-prefix("http://junkuchan.org/a/") {

.logo {background: url('http://img188.imageshack.us/img188/2350/dollarslogo.png') no-repeat 50% 0px !important; height:61px; color:#000 !important; font-size:smaller !important; text-align:left !important; }

.logo img {display:none;}

.postarea {background: #FCFCFC !important; color:#000 !important; position:relative; width:500px; margin:0 auto; -moz-border-radius:20px;padding:10px 10px 0;}

.postarea form .postform {padding-bottom:40px; position:relative;}

.postarea .rules li, .postarea .rules a {font-size:smaller !important;}

.postarea input[type='text'], .postarea input[type='password'], .postarea textarea {color:#eee !important;}

.postarea input:focus, .postarea textarea:focus {background-color:#ccc !important;color: #000 !important;}

.postarea td input[type='file']{color:#000 !important;}

.postarea input[type='submit'] {position:absolute; bottom:10px; margin-left:-50%; left:auto; background:-moz-linear-gradient(top,#FAFAFA, #FAFAFA 47%,#E8E8DC 50%, #E8E8DC) !important; font-family:verdana,sans-serif; font-weight:bold; font-size:1.5em; text-transform:uppercase; text-align:center; width:300px; height:30px; border-width:3px !important; -moz-border-radius:10px; color:#000 !important;}

.postarea input[type='submit']:hover {background:-moz-linear-gradient(top,#C9C9C0, #C9C9C0 47%,#7A7A74 50%, #7A7A74) !important; color:#fff !important;}

div[id^='thread'] > table {clear:both;}

.doubledash {display:none !important;}

td.reply {float:left;}

td.reply, td.highlight, td.replyhl {color:#fff !important; font-weight: bold;position:relative !important; border-color:#fff !important; -moz-border-radius:10px; border-width:3px !important; border-style: solid !important; display:block; margin-left:120px !important; padding-bottom:10px;}

td.reply {background:-moz-linear-gradient(top, #7CADD3,#7CADD3 27px, #446CAE 30px, #446CAE) !important;}

td.highlight, td.replyhl, table:nth-of-type(2n) > tbody > tr > td.reply {background:-moz-linear-gradient(top, #F7BD44,#F7BD44 27px, #E07414 30px, #E07414) !important;}

/*color changed*/
.postername, .commentpostername {font-weight:bold !important; display:block; color:rgb(166,172,170) !important; background-repeat:no-repeat !important; background-position:50% 0 !important;}

td.reply .postername, td.highlight .postername, td.reply .commentpostername, td.replyhl .commentpostername {display:block; position:absolute; left:-100px; width:100px; text-align:center; clear:both; padding-top:50px; top:0px; overflow:visible; margin:0 !important;}

td.reply .postername, td.reply .commentpostername {background-image:url('http://img97.imageshack.us/img97/9367/lilanon.jpg') !important;}

td.highlight .postername, td.replyhl .commentpostername {background-image:url('http://img718.imageshack.us/img718/7435/lilanonorange.jpg') !important;}

.postername:before, .commentpostername:before {display:block; width:22px; height:22px; position:absolute; left:78px;top:16px;}

td.reply .postername:before, td.reply .commentpostername:before {content:url('http://img33.imageshack.us/img33/99/bubblearrow.png');}
/*table:nth-of-type(2n+1) > tbody > tr > td.reply*/
td.highlight .postername:before, td.replyhl .commentpostername:before, table:nth-of-type(2n) > tbody > tr > td.reply .postername:before, table:nth-of-type(2n) > tbody > tr > td.reply .commentpostername:before{content:url('http://img188.imageshack.us/img188/3659/bubblearroworange.png');}

div[id^='thread'] > label > .postername {float:left; margin-top:10px; margin-right:10px;}

.reply a {color:#4B2D8B !important;}

.reply a:visited {color:#2A0679 !important;text-decoration:line-through;}

.reply a:hover {color:#B59228 !important;}

.postername a, .commentpostername a {background: none transparent !important; color:#8BCBFE !important; font-size:11pt !important; font-weight:bold !important;display:block;padding-top:50px; margin-top:-50px; background:url('http://img188.imageshack.us/img188/1548/lilanonblue.jpg') no-repeat 50% 0 !important;}

/*added to highlight tripfags*/
/*.postername + .postertrip, .commentpostername + .postertrip {background: none transparent !important; color:#8BCBFE !important; font-size:11pt !important; font-weight:bold !important;display:block;padding-top:50px; margin-top:-50px; background:url('http://img188.imageshack.us/img188/1548/lilanonblue.jpg') no-repeat 50% 0 !important;}*/
span:not(:only-of-type):nth-child(1)[class="postername"] {background-image:url('http://img718.imageshack.us/img718/7435/lilanonorange.jpg') !important;}

/*.postertrip:only-of-type {background: none transparent !important; color:#8BCBFE !important; font-size:11pt !important; font-weight:bold !important;display:block;padding-top:50px; margin-top:-50px; background:url('http://img188.imageshack.us/img188/1548/lilanonblue.jpg') no-repeat 50% 0 !important;}*/

/*td.highlight, td.replyhl {background:-moz-linear-gradient(top, #F7BD44,#F7BD44 27px, #E07414 30px, #E07414) !important;}
//end*/

.unkfunc {color:#EEA211 !important; padding-left:10px;}

.reflinkpreview {z-index:100;margin:-50px 0 0 -50px;}

.reflinkpreview .reply {display:block !important; min-width:100px !important;}

body {background:#000 !important;color:#fff !important;}

}
