css:
@-moz-document url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com")  {
  section-content.profile-header-content.ng-scope {
  border-radius: 12px;
}
.col-sm-6.section-content.profile-avatar-left {
  border-radius: 12px 0 0 12px;
}
img {
  border-radius: 12px;
}
.age-rating-details.col-xs-12.section-content {
  border-radius: 50px;
}
li.stack-row.badge-row.ng-scope {
  border-radius: 100px;
  margin-bottom: 10px;
}
button.btn-full-width.btn-control-sm.ng-binding {
  border-radius: 50px
}
.dark-theme .thumbnail-2d-container {
  background-color: #00000000;
}
button.btn-full-width.btn-common-play-game-lg.btn-primary-md.btn-min-width {
  border-radius: 30px;
}
.cart-item {
  border-radius: 12px;
}
button.btn-more.rbx-private-server-create-button.btn-secondary-md.btn-min-width {
  border-radius: 30px;
}
a#upgrade-now-button {
  border-radius: 30px;
}
div#header {
  margin: 10px;
  border-radius: 30px;
}
.alert.alert-warning {
  height: 40px;
  border-radius: 30px;
}
.alert.alert-success {
  height: 40px;
  border-radius: 30px;
}
.content {
  margin-top: 10px;
}
.shimmer {
  border-radius: 12px;
}
ul.dropdown-menu {
  border-radius: 20px 0 0 20px;
  margin-bottom: 10px;
}
button#language-switcher {
  border-radius: 30px;
}
div#navigation {
  margin: 10px;
  margin-top: 20px;
  border-radius: 20px;
  height: fit-content;
  padding-bottom: 10px;
}
.btn-navigation-nav-menu-md {
  border-radius: 30px;
}
div#games-carousel-page {
  margin-left: 10px;
}
.scroller.prev {
  border-radius: 20px;
  }
}
