css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.symfony-project.com/book/trunk") {

#footer, #bar, .weblog_comment, #header, #navigation, #topseparator
{
    display: none !important;
}

table#topbar h1
{
    padding-top: 16px !important;
    background-color: #eee !important;
    color: #000 !important;
}

table#topbar h2
{
    background-color: #eee !important;
    color: #000 !important;
}

#content1, .documentation
{
    margin: 0px !important;
    font-size: 1.2em !important;
}

.documentation pre
{
    background-color: #fafafa !important;
    overflow: visible !important;
}

.documentation pre.command-line
{
    background-color: #eee !important;
    color: #000 !important;
}

.documentation blockquote.sidebar
{
    background-color: #eee !important;
    color: #000 !important;
}

.documentation blockquote.sidebar p
{
    background-color: #eee !important;
    color: #000 !important;
}

.documentation blockquote.sidebar p.title
{
    background-color: #ddd !important;
    color: #fff !important;
}

}
