css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
               domain("maps.google.com"),
               domain("maps.google.de"),
               domain("ditu.google.com"),
               domain("ditu.google.cn"),
               domain("maps.google.nl"),
               domain("maps.google.co.uk"),
               domain("maps.google.ca"),
               domain("maps.google.com.au"),
               domain("maps.google.fr"),
               domain("maps.google.ca"),
               domain("maps.google.it"),
               domain("mapy.google.pl"),
               domain("maps.google.co.jp"),
               domain("maps.google.es") 
{

.helplinks { 
z-index: -999 !important;
  display:none !important; 
overflow:hidden !important;   
  width:0px !important; 
  height:0px !important;
}

#logo,#srchex,#links {
display:none;
}

#topads,#bottomads {
opacity:0.5 !important;
 display:none;
}


#map {
z-index: 0 !important;
opacity:1 !important;
position:fixed !important;
  right:0px !important;
  top:0px !important;
  width:100% !important;
  height:100% !important;
}

#panel {
z-index: 4 !important;
top: 19px !important;
background-color: white;
/*visibility:hidden !important;*/
}

.bar_icon_print2 {
  position:fixed !important;
  top:2px !important;
  left:60px !important;
}
.bar_icon_email2 {
  position:fixed !important;
  top:2px !important;
  left:40px !important;
}
.bar_icon_link2 {
  position:fixed !important;
  top:2px !important;
  left:20px !important;
}

.controls {
  position:fixed !important;
  top:-15px !important;
  right:200px !important;
}
#gbar,#guser {
  visibility:hidden !important; 
}
#ds-h {
visibility:hidden !important;
}


#q_form div.srchcol {
  z-index: 9999 !important;
  position:fixed !important;
  top:3px !important;
  right:400px !important;
  height: 20px !important;
  background-color: rgba(0,0,0,0.3) !important;
  padding: 5px !important;
}

#q_d {
width: 100px !important;
}

#hidess,#showss {
color: white !important;
}


}
