css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rapidsearch.yi.org") {
  body, table, td, th { font:14px/1.4 sans-serif !important; }
  body { text-align:left !important; margin:8px !important; padding:0 !important; color:#EEF !important;}
  body, table[border="1"] tr {
     background:#35577D url(data:image/jpeg;base64,) 0 0 no-repeat !important;
  }

  a, 
  a:link,
  a:active  { color:#BCE !important; text-decoration:none !important; }
  a:visited { color:#9AC !important; text-decoration:none !important; }
  a:hover   { color:#FFF !important; text-decoration:underline !important; }
  input {border:1px groove #416A99; padding:0 .2em; color:#AAC; background:#40638B; max-width:40em; }

  input[type="text"], 
  input[type="submit"] { margin:1px !important;  }


  center>table { width:100% !important; }
  script+span  { display:none; }

  table[border="1"] {
    width:100% !important;
    border-collapse:collapse !important;
    border-spacing:0 !important;
    border-color:#4A719C !important;

  }
  table[border="1"] tr:first-child {
    background:#4A719C !important;
  }
  table[border="1"] td,
  table[border="1"] th {
    padding:2px .2em !important;
  }

  table[border="1"] b[title^="Old"]   { color:#F55 !important; font-weight:normal; }
  table[border="1"] tr td             { white-space:nowrap; }
  table[border="1"] tr td:first-child { white-space:normal; }
  
  table[border="1"] td:first-child>a[href^="http://"]:before {
    content:"[-?-] "; 
    font:.8em monospace; 
    color:#AAA;
  }
  
  table[border="1"] td:first-child > a[href*="rapidshare.com"]:before {
    content:"[rsc] " !important; color:#338 !important; 
  }
  table[border="1"] td:first-child > a[href*="rapidshare.de"]:before {
    content:"[rsd] " !important; color:#33F !important;
  }
  table[border="1"] td:first-child > a[href*="megaupload"]:before { 
    content:"[mul] " !important; color:#833 !important; 
  }
  table[border="1"] td:first-child > a[href*="gigeshare"]:before { 
    content:"[ggs] " !important; color:#5E5 !important; 
  }
  table[border="1"] td:first-child > a[href*="megarotic"]:before { 
    content:"[mgr] " !important; color:#D77 !important; 
  }
  table[border="1"] td:first-child > a[href*="mediafire"]:before { 
    content:"[mdf] " !important; color:#911 !important; 
  }
  table[border="1"] td:first-child > a[href*="sendspace"]:before { 
    content:"[ssp] " !important; color:#22A !important; 
  }
  table[border="1"] td:first-child > a[href*="savefile"]:before { 
    content:"[sfl] " !important; color:#AAF !important; 
  }
}
