css:
@-moz-document domain("cozy.tv") {
body {
    background-color: white;
    color: #000;
}
#app {
    color: #000;
}
.text-gray-400 {
    color: #7f7f7f;
    font-weight: normal;
}
.text-gray-300 {
    color: #000;
    font-weight: normal;
}
.rounded {
    border-radius:0;
}
.border-gray-600 {
    border: 3px solid #c0c0c0;
}
.border-b.border-gray-600 {
    border-width: 0;
    border-bottom-width: 1px;
    border-color: #7f7f7f;
}
.bg-gray-850 {
    background-color: transparent;
}
.bg-gray-600 {
    background-image: linear-gradient(to right, #000081, #257fb3);
    color: white;
}
/* BUTTON */
.relative.flex.items-center.rounded.font-mont.font-medium.text-base.px-2.py-1.rounded.bg-gray-500, .text-gray-200.bg-gray-600, .font-medium.bg-gray-600, a.bg-gray-600 {
    background: none;
    background-color: #c0c0c0;
    color: #000;
    border: 1px solid white;
    border-bottom-color: #000;
    border-right-color: #000;
    border-radius: 0;
    font-weight: normal;
}
.relative.flex.items-center.rounded.font-mont.font-medium.text-base.px-2.py-1.rounded.bg-gray-500:active, .text-gray-200.bg-gray-600:active, .font-medium.bg-gray-600:active, a.bg-gray-600:active {
    background-color: #dedede;
    border-color: #000;
    border-bottom-color: white;
    border-right-color: white;
}
/* MENU BUTTON */
.h-full.relative.inline-block.text-left > .flex.items-center > .w-full {
    color: #000;
}
/* REPORT BUTTON */
.h-full.relative.inline-block.text-left > .w-56 {
    border-radius: 0;
    background-color: #c0c0c0;
    border: 1px solid white;
    border-bottom-color: #000;
    border-right-color: #000;
}
.h-full.relative.inline-block.text-left > .w-56:active {
    border: 1px solid #000;
    border-bottom-color: white;
    border-right-color: white;
}
.h-full.relative.inline-block.text-left > .w-56 .p-1 {
    padding: 0;
}
.h-full.relative.inline-block.text-left > .w-56 .px-4.py-2 {
    padding: 0;
    padding-left: 0.5rem;
    color: #000;
    font-weight: normal;
}
.h-full.relative.inline-block.text-left > .w-56 .px-4.py-2:hover {
    background-color: #000081;
    color: white;
}
.h-full.relative.inline-block.text-left > .w-56 .px-4.py-2:active {
    background-color: #dedede;
    color: #000;
}
/* TIME AND PEOPLE */
.text-gray-300.bg-gray-500, .text-white.bg-gray-500 {
    background-image: none;
    background-color: #c0c0c0;
}
.text-gray-300.bg-gray-500 .text-gray-200, .text-white.bg-gray-500 .text-sm {
    color: #000;
    font-weight: normal;
}
.absolute.flex.items-center.bottom-0.right-0.z-10.rounded-sm.pointer-events-none.text-xs.m-2 {
    /* HOME PEOPLE */
    color: white;
    font-weight: bold;
}
.hover\:bg-blue-200:hover {
    background-color: #000081;
    color: white;
}
.hover\:bg-blue-200:hover .czbtnfancy, .hover\:bg-blue-200 .czbtnfancy {
    transition: none;
}
input {
    background: white;
    color: #000;
    border-radius: 0 !important;
}
input::placeholder {
    color: #000;
}
.color-links a {
    color: #000081;
}

/* LEFT COLUMN */
.flex.flex-col.w-60.border-r.border-gray-600 {
    padding-left: 24px;
    border:0;
    background-color: #c0c0c0;
    color: #000;
}
.flex.flex-col.w-60.border-r.border-gray-600::before {
    position: absolute;
    background-color: #000;
    background-image: linear-gradient(to bottom, transparent, #0000fe);
    width: 24px;
    height: 100%;
    z-index: -1;
    content:"";
    top:0;
    left: 0;
}
/* LOGO */
.flex.flex-col.w-60 .px-5.py-2 {
    /*
    border: 1px solid white;
    border-right-color: #000;
	border-bottom-color: #000;
	background-color: #c0c0c0;
    */
    background-color: #000;
    padding-bottom: 1rem;
}
/* TOPBAR */
nav.nav-shadow {
    /*max-height: 34px;*/
    box-shadow: none;
    background-color: #c0c0c0;
    color: #000;
    background-image: linear-gradient(to right, #000081, #257fb3);
    color: white;
    border:0;
}
nav.nav-shadow.border-b {
    border-bottom: 3px solid #c0c0c0;
}
/* PAGES */
nav.nav-shadow .px-4.py-2.font-bar.font-bold.uppercase {
    padding: 0;
    padding-right: 1rem;
    font-weight: normal;
    text-transform: lowercase;
}
nav.nav-shadow .px-4.py-2.font-bar.font-bold.uppercase::first-letter {
    /*text-decoration: underline;*/
    text-transform: uppercase;
}
/* SEARCHBAR */
nav.nav-shadow input {
    background-color: #c0c0c0;
    border: 1px solid white;
    border-right-color: #000;
    border-bottom-color: #000;
}
nav.nav-shadow input:active, nav.nav-shadow input:focus {
    background-color: #dedede;
    border: 1px solid #000;
    border-right-color: white;
    border-bottom-color: white;
}

/* STREAK */
/*nav.nav-shadow .font-mont.mr-4.flex.items-center.select-none {}*/

/* ARTICLE */
.flex.flex-col.w-full.h-full.overflow-hidden {
    border: 3px solid #c0c0c0;
}
.overflow-y-scroll.no-scrollbar.font-bar.flex.flex-grow.h-0.notbody {
    border: 0;
}
.flex.items-start.p-2.text-sm.lg\:text-base {
    background-color: #c0c0c0;
}
.flex.flex-col.items-center.w-full.bg-gray-850 .font-bar {
    color: #000;
}
.flex.flex-col.items-center.w-full.bg-gray-850 > .w-full > .flex-grow {
    background-color: #008081;
    padding-top: 2rem;
}
.flex.flex-col.items-center.w-full.bg-gray-850 > .w-full.flex.flex-grow.flex-col{
    background-color: #c0c0c0;
}
div.overflow-x-hidden:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(2)
/* ABOUT BOX */
.w-full.h-full.bg-gray-850,
.flex.flex-col.items-center.w-full.bg-gray-850 .md\:bg-gray-700 {
    background-color: transparent;
}
/* ABOUT|REPLAYS */
.sticky.bg-gray-850 {
    background-color: #c0c0c0;
}
.sticky.pt-5 {
    /*padding-top: 6px;*/
    margin-top: 1.3rem;
    padding-top: 1rem;
    font-size: 1rem;
}
.sticky .text-white, .sticky .text-gray-300 {
    color: #000;
}
/* CHAT */
.flex-shrink-0.h-full.border-l.border-gray-600 {
    border: 0;
    border-left: 3px solid #c0c0c0;
}
.relative.flex.justify-center.p-4.text-sm {
    background-image: linear-gradient(to right, #000081, #257fb3);
    border-bottom: 3px solid #c0c0c0;
    color: white;
}
.w-full.h-full.overflow-y-scroll.no-scrollbar, /*BG*/
.flex.flex-col.w-full.overflow-hidden.rounded {
    background-color: white;
    background-image: linear-gradient(to bottom right, #7bbde7, white 25%);
    border-radius: 0;
}
.bg-black.bg-opacity-70 {
    background-color: transparent;
    position: relative;
    color: #000;
    font-weight: normal;
    opacity: 1;
    border: 1px solid white;
    border-bottom-color: #000;
    border-right-color: #000;
}
.bg-black.bg-opacity-70::before {
    opacity: 70%;
    position: absolute;
    width: 100%;
    height: 100%;
    content:"";
    top: 0;
    left: 0;
    z-index: -1;
    background-color: #c0c0c0;
}
/* CHAT BOX */
.w-full.p-2.px-2.bg-gray-800 {
    background: none;
    background-color: #c0c0c0;
    color: #000;
    padding: 0;
    padding-top: 3px;
    border: 0;
}
.bg-gray-800 .mb-1 {
    margin-bottom:0;
}
.text-sm.font-medium.text-gray-300 {
    color: white;
}
.text-\[\#f3f7f6\] {
    color: #000;
}
.text-\[\#96b4f1\] {
    color: #000081;
}
.bg-\[\#eb0400\] {
    /* LIVE */
    color: white;
}
.text-blue-300 {
    color: #1181cb;
}
.text-gray-300 .text-white {
    /* HOME VERIFIED */
    color: #7f7f7f;
}
.inline.w-5.h-5.mr-1.rounded-full {
    fill: #000;
}
.absolute.z-10.rounded-sm.right-0.bottom-0.px-2.py-px.bg-gray-500.font-semibold.text-sm.m-1, .absolute.z-10.rounded-sm.right-0.bottom-0.px-2.py-px.bg-black.bg-opacity-80.font-semibold.text-sm.m-1 {
    /* HOME TIME */
    background-image: none;
    background-color: transparent;
    color: white;
    font-weight: bold;
}
.bg-gray-600.input-shadow {
    background: none;
    background-color: white;
    color: #000;
    border-radius: 0;
}
.bg-gray-600.input-shadow .text-gray-200\/50 {
    color: #c0c0c0;
}
/* SHOW MORE */
.font-medium.text-gray-400.px-4.py-2.border-t.border-gray-600.text-xs.uppercase {
    border: 0;
    border-top: 1px solid #7f7f7f;
    font-weight: normal;
}
.font-medium.text-gray-400.px-4.py-2.border-t.border-gray-600.text-xs.uppercase:hover {
    color: #000;
}
/* SEARCH RESULT */
.bg-gray-600 li:hover {
    background-color: rgba(0, 0, 0, 0.1);
}
/* SETTINGS */
.bg-gray-950 {
    background-color: #008081;
    color: white;
}
/* STICKERS */
.bg-gray-500 {
    background-color: white;
    background-image: linear-gradient(to bottom right, #7bbde7, white 25%);
    color: #000;
    font-weight: normal;
}
.bg-gray-500.rounded-tl-sm.rounded-tr-sm {
    background: none;
    background-color: #008081;
    color: white;
}
/* DEFAULT USER */
.inline-block.align-middle.w-6.h-6.mr-2.mt-1.select-none > svg {
    background-color: #008081;
}
}
