===/USERSTYLE========= */

/* =====BACKGROUND====== */
div#editor-wrapper,
body {

    background: url('https://i.imgur.com/nFSzM2v.jpg');
    background-repeat: no-repeat;
    background-position-x: center, center, left;
    background-position-y: 0px, bottom, top;
}

/* ==DARKMODE-BUTTON== */
.dark-mode-switch.gmstyle {
    top: 5px;
    left: 22px;
    filter: brightness(1);
    opacity: 0;
}


.dark-mode-switch {
    top: 5px;
    left: 22px;
    filter: brightness(1);
    opacity: 0;
}



/* ==RIGHTSIDERBAR-CONTROL== */
div#sidebarcontrol {
    background: url('https://i.imgur.com/qddgz5K.png') no-repeat center;
    opacity: 1;
    top: -45px;
    text-align: center;
    height: 1100px;
    width: 90px;
    font-size: 0px;
    border: 0px;
    color: #fff0;
    filter: brightness(0.6);
}

#sidebarcontrol.alertify {
    background-color: #b381fcad;
    -webkit-animation: colorPulse 0s infinite alternate;
    width: 95px;
}



/* ===PLAYER TOOLBAR====== */
#secondary-toolbar .tokenactions .btn,
.btn.btn-default {
    color: #fab3e7;
    font-weight: 300;
    box-shadow: inset 3px 3px 3px 3px rgba(18, 1, 1, .75), 2px 2px 2px 2px #000000c9;
    border: 0px;
    border-radius: 8px;
    background-color: rgba(0, 0, 0, .75);
    margin-bottom: 5px;
    margin-right: 6px;
}

#secondary-toolbar .btn:hover {
    color: #ebc876;
    font-weight: 600;
    border: 2px;
    box-shadow: inset 3px 3px 3px 3px rgba(18, 1, 1, .75), 2px 2px 2px 2px #000000c9;
    border-radius: 8px;
}


#secondary-toolbar {
    width: 100%;
    top: 54px;
    height: 72px;
    background-color: rgba(148, 132, 191, .59);
    text-align: center;
    left: 0px;
    border: 0px;
}

#secondary-toolbar li {
    border: none;
    padding: 0 5px;
}


/* ======FIXED MACROBAR======= */
#macrobar {
    width: 98.1%;
    top: 0px;
    height: 53px;
    background-color: rgba(82, 32, 109, .49);
    text-align: center;
    left: 0px;
    border: 0px;
}
div.macrobox button.btn {
    padding: 0 4px 0 4px;
    font-size: 1.5rem;
    line-height: 1.8rem;
    margin: 0px 0 2px 0;
}
#macrobar_macros {
    white-space: normal !important;
    margin-left: 1% !important;

    padding-bottom: 0px;
}
#macrobar .macrobox {
    margin: 1px 5px 1px 5px;
}
#macrobar div.macrobox:hover,
#macrobar div.macrobox.ui-sortable-helper {
    padding-right: 12px;
    margin-right: -7px;
    background-color: #bf7cfc00;
    cursor: move;
}


/* ==LEFT TOOLBAR== */
div#floatingtoolbar {
    font-family: 'Montserrat', sans-serif;
    font-weight: 600;
    top: 135px;
    border-radius: 8px;
    background-color: #d0bcef;
    
         
}

div#floatingtoolbar li {
    border: 0px;
    border-radius: 6px;
       color: #333;

}




div#floatingtoolbar li:hover,
#floatingtoolbar li.activebutton,
#zoompanel ul li.zoomClickBack,
