/*pozadi bloku*/
 .Style__Bright--Variant1 .Background--LightGray, .Style__Bright--Variant2 .Background--LightGray, .Style__Bright--Variant3 .Background--LightGray, .Style__Bright--Variant4 .Background--LightGray, .Style__Bright--Variant5 .Background--LightGray {
    background-color: #F2F2F2;
}
/*tvar informačního bloku papametry vlastnosti ticketu vše do řádky
 .Columns__Three .Widget__Content .col-lg-2, .Columns__Three .Widget__Content .col-lg-3, .Columns__Three .Widget__Content .col-lg-4, .Columns__Three .Widget__Content .col-lg-6, .Columns__Three .Widget__Content .col-xl-2, .Columns__Three .Widget__Content .col-xl-3, .Columns__Three .Widget__Content .col-xl-4, .Columns__Three .Widget__Content .col-xl-6, .Columns__Two .Widget__Content .col-lg-2, .Columns__Two .Widget__Content .col-lg-3, .Columns__Two .Widget__Content .col-lg-4, .Columns__Two .Widget__Content .col-lg-6, .Columns__Two .Widget__Content .col-xl-2, .Columns__Two .Widget__Content .col-xl-3, .Columns__Two .Widget__Content .col-xl-4, .Columns__Two .Widget__Content .col-xl-6 {
