/*nadpisy bloku*/
 .h1, .h2, h1, h2, h3, h4, h5 {
    font-size: 18px;
    font-size: 1.125rem;
    font-weight: 600;
}
/*zesílení textu bloky, info poznámek v ticketu*/
 body, html {
    font-family: Source Sans Pro, Helvetica, Arial, sans-serif;
    font-weight: 700;
}
/*zvýrazní pruh interní poznámky*/
 .BusinessObjectCommunicationStream .Article .Badge {
    display: block;
    white-space: normal;
}
/*podparveni zvyrazneni interni poznámky*/
 .Badge--VisibilityInternal {
    background-color: #d8cfff;
}
/*postranni blok pri rozdelovani ticketu*/
 .CommonFlyout--Show.CommonFlyout--Medium {
    width: 40%;
}
/*zhusteni textu vlastnosti ticketu*/
 body {
    line-height: 1;
}
/*zvyrazneni povinnych polozek, odsazeni a vykricnik*/
[dir=ltr] form .required:before {
    margin-right: 0.200rem;
}
form .required::before {
    content:"*";
    font-size: x-large;
}
/*odsazeni tlacitka zpetnahoru doleva*/
[dir="ltr"] .App__ScrollToTop__Button {
    right: 50rem;
}
/*podbarvi bloky ve vlastnostech ticketu*/
 .Style__Bright--Variant1 .bg-white, .Style__Bright--Variant1 .modal-content, .Style__Bright--Variant2 .bg-white, .Style__Bright--Variant2 .modal-content, .Style__Bright--Variant3 .bg-white, .Style__Bright--Variant3 .modal-content, .Style__Bright--Variant4 .bg-white, .Style__Bright--Variant4 .modal-content, .Style__Bright--Variant5 .bg-white, .Style__Bright--Variant5 .modal-content {
    background-color: #5424990a !important;
}
/*obarvi nadpisy a hlavní texty ticketu vlastnostech ticketu*/
 .Style__Bright--Variant1 .h1, .Style__Bright--Variant1 .h2, .Style__Bright--Variant1 h1, .Style__Bright--Variant1 h2, .Style__Bright--Variant1 h3, .Style__Bright--Variant1 h4, .Style__Bright--Variant1 h5, .Style__Bright--Variant2 .h1, .Style__Bright--Variant2 .h2, .Style__Bright--Variant2 h1, .Style__Bright--Variant2 h2, .Style__Bright--Variant2 h3, .Style__Bright--Variant2 h4, .Style__Bright--Variant2 h5, .Style__Bright--Variant3 .h1, .Style__Bright--Variant3 .h2, .Style__Bright--Variant3 h1, .Style__Bright--Variant3 h2, .Style__Bright--Variant3 h3, .Style__Bright--Variant3 h4, .Style__Bright--Variant3 h5, .Style__Bright--Variant4 .h1, .Style__Bright--Variant4 .h2, .Style__Bright--Variant4 h1, .Style__Bright--Variant4 h2, .Style__Bright--Variant4 h3, .Style__Bright--Variant4 h4, .Style__Bright--Variant4 h5, .Style__Bright--Variant5 .h1, .Style__Bright--Variant5 .h2, .Style__Bright--Variant5 h1, .Style__Bright--Variant5 h2, .Style__Bright--Variant5 h3, .Style__Bright--Variant5 h4, .Style__Bright--Variant5 h5 {
    color: #573dd0ba;
}

/*obarvi ikonky v organizeru*/
.Style__Bright--Variant1 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Bright--Variant2 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Bright--Variant3 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Bright--Variant4 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Bright--Variant5 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Dark--Variant1 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Dark--Variant2 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Dark--Variant3 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Dark--Variant4 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__Dark--Variant5 .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__HighContrastBright .Organizer__Nav__Item .Organizer__Nav__Item__Button, .Style__HighContrastDark .Organizer__Nav__Item .Organizer__Nav__Item__Button {
  color: #cac838e6;
}

/*vypne avatara*/
.Avatar__Size--LG {
  height: 0px;
  width: 0px;
}
.Avatar {
  margin: 0rem 0;
}

/*pozadí varovných zpráv storno (ANO / NE)*/
.Style__Bright--Variant1 .bg-white, .Style__Bright--Variant1 .modal-content, .Style__Bright--Variant2 .bg-white, .Style__Bright--Variant2 .modal-content, .Style__Bright--Variant3 .bg-white, .Style__Bright--Variant3 .modal-content, .Style__Bright--Variant4 .bg-white, .Style__Bright--Variant4 .modal-content, .Style__Bright--Variant5 .bg-white, .Style__Bright--Variant5 .modal-content {
    background-color: #f1f3f4 !important;
}
