css:
@-moz-document domain(app.shkolo.bg)
{


  
  .btn-custom-primary
  {
    background-color: white !Important;
    border-color: black !important;
    color: black !important;
  }

  .support-box
  {
    background-color: #f077f2 !important;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  }

  .page-content
  {
    background-size: 100% auto;
    background-image: url("https://media.discordapp.net/attachments/1029652433258950677/1029652622837301298/100-1008594_s.jpg");
  }

  .login
  {
    background-size: 100% auto;
    background-image: url("https://media.discordapp.net/attachments/1029652433258950677/1029652622837301298/100-1008594_s.jpg");
  }

  .portlet.light
  {
    opacity: 0.7;
  }

  .portlet.light:hover
  {
    opacity: 1;
  }

  .compactTableRow
  {
    opacity: 0.7;
  }

  .compactTableRow:hover
  {
    opacity: 0.7;
  }

  .page-header.navbar
  {
    background-color: #eb15d9;
  }

  .page-footer
  {
    color: black;
    background-color: #eb15d9;
  }

  .page-sidebar,.page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover
  {
    background-color: white;
  }

  .page-sidebar .page-sidebar-menu>li>a, .page-sidebar-closed.page-sidebar-fixed .page-sidebar:hover .page-sidebar-menu>li>a
  {
    border-top: 1px solid #4a83b6;
    color: #000000;
    font-weight: 600;
  }

  .btn-info
  {
    background-color: red;
    border-color: black;
  }

  .btn-info:hover
  {
    background-color: #990000;
    border-color: black;
  }

  .alert-warning
  {
    background-color: white;
    border-color: black;
    color: black;
    font-weight: bold;
  }

  .fa-bullhorn
  {
    color: white !important;
  }

  .fa-envelope-open
  {
    color: white !Important;
  }

  .icon-bell
  {
    color: white !important;
  }

  .username
  {
    color: white !important;
  }

  .fa-sign-out
  {
    color: white !important;
  }

  .support-text
  {
    color: black !important;
    margin-top: 5px;
  }

  .phone-label
  {
    color: black !important;
    background-color: white !important;
    ;
  }

  body
  {
    background-color: #eb15d9;
  }
}
