/* ==UserStyle==
@name           Soundcloud Pitch Black
@namespace      USO Archive
@author         stormi
@description    `Soundcloud in dark :)For the nice font in the screenshot download & install this font: <a href="http://www.dafont.com/champagne-limousines.font">Champagne & Limousines</a> - For even more black & white experience you can additionally install <a href="https://userstyles.org/styles/107072/soundcloud-grayscale">Soundcloud Grayscale</a> --- Check out my other "Dark" styles:  ₪ <a href="https://userstyles.org/styles/50113">Google</a>  ₪ <a href="https://userstyles.org/styles/44530">Discogs</a>  ₪ <a href="https://userstyles.org/styles/155744">TabletPCReview & NotebookReview Forum</a>  ₪ <a href="https://userstyles.org/styles/43733">Computerbase</a>  ₪ <a href="https://userstyles.org/styles/31267">Global dark style</a>  ₪ <a href="http://userstyles.org/users/43566">and more...</a>`
@version        20201109.22.19
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix("http://soundcloud.com"),
url-prefix("https://soundcloud.com") {
    /*** GENERAL ***/
    [aria-role="img"] {
        border-radius: 0px;
        border-width: 2px;
        border-style: solid;
        border-image: radial-gradient(circle, rgba(255,255,255,0.6979166666666667) 0%, rgba(62,62,62,1) 100%) 1;
    }
    #l-container l-content {
        background-image: url("https://i.imgur.com/hnWHAn3.png") !important;
        background-repeat: repeat;
    }
    #playControls {
        background-color: #212121 !important;
    }
    html,
    .social-sign-in-separator span,
    .readMoreBadge__countWrapper {
        background-image: url("https://i.imgur.com/hnWHAn3.png") !important;
        background-repeat: repeat;
    }
    body,
    .site,
    .dashbox,
    .listenContent__inner,
    .sound__soundActions,
    .l-footer,
    .activity__sharingNote-container,
    .commentForm,
    .commentForm__inputWrapper,
    .commentForm__wrapper,
    .createPlaylist:hover,
    .createPlaylist.focused,
    .uploadStatus,
    .activeUpload__section,
    .activeUpload__quietTeaser,
    .activeUpload__blockTable.threeCell,
    .autoTagger__fields,
    .sc-background-light,
    .addToPlaylistTabs .tabs__tabs,
    .g-modal-section,
    .addToPlaylistItem__actions,
    .createPlaylistSuggestion__addContainer,
    .conversation__actions,
    .conversation__form,
    .inbox__item:before,
    .inboxItem.active,
    .inboxItem:hover,
    .g-form-section.highlight:hover,
    .advancedFields__quietTeaser,
    .sharePanel .tabs__tabs,
    .embedPanel__tabLink,
    .manageSounds__upsellWrapper,
    .uploadMain__foot,
    .searchTitle__content,
    .audibleEditForm__form,
    .tabs__tabs,
    .tabs__headingContainer,
    .commentItem.m-creatorComment,
    .trackManager__upsellWrapper,
    .linkMenu,
    .activeUpload__shareContainer,
    .profileUploadFooter,
    .quotaMeterWrapper,
    .sc-background-white,
    .statsOverview__separator {
        background: url("https://i.imgur.com/hnWHAn3.png") !important
    }
    .commentForm,
    .commentForm__inputWrapper,
    .commentForm__wrapper,
    .sc-button-group,
    .sound__footer.sc-border-light-bottom,
    .header__soundInput,
    .listenEngagement,
    .searchTitle__content,
    h1.searchTitle__text {
        border: none !important
    }
    .conversation__actions,
    .conversation__form,
    .listenEngagement {
        box-shadow: none !important
    }
    .dashbox,
    .listenDetails__login,
    article.whoToFollowModule,
    .header__goProWrapper,
    .header__right > a.header__link,
    .statsBadge__upsell,
    .editAccessTab__quietTeaser,
    .stream > .stream__header,
    .searchPremiumContentHeader,
    .searchPremiumContentFooter,
    .searchPremiumContentHeader + .searchList__item,
    .streamHTUpsell {
        display: none !important
    }
    #flashAudioObject-container {
        visibility: hidden !important
    }

    body,
    .sc-text,
    .resultCounts,
    .g-nav-link,
    h3.conversationBadge__senderName,
    .sc-type-h3,
    .conversation .sc-link-dark,
    .active .trackItem__trackTitle {
        color: #BBB !important
    }
    .sc-link-verylight,
    .sc-text-verylight {
        color: #444 !important
    }
    .sc-link-light:hover,
    .g-nav-item.active > .g-nav-link {
        color: #FFF !important
    }
    .sc-link-dark,
    .dropdownList__loading,
    .sound__uploadTime,
    .compactTrackListItem__trackTitle,
    .queueItemView__title {
        color: #666 !important
    }
    .sc-link-dark:hover,
    .sc-link-verylight:hover,
    .trackPreviewItem__titleLink:hover,
    .g-tabs-item:not(.active) .g-tabs-link:hover,
    .g-link-problem:hover,
    .activeUpload__sectionTitle,
    .blockCheckbox__title,
    .autoTagger__title {
        color: #DDD !important
    }
    .userBadge.large .userBadge__title,
    .userBadge.large .userBadge__usernameLink {
        color: #06C !important
    }
    .active .soundTitle__title,
    .active .compactTrackListItem__trackTitle,
    .trackItemWithEdit.active .trackItemWithEdit__trackTitle,
    .userBadge.large .userBadge__usernameLink:hover,
    .g-nav-link:hover,
    .listenInfoTeaser__description a:hover,
    .m-playing .queueItemView__title,
    .m-playing .queueItemView__title * {
        color: #F60 !important
    }

    /*
#app h1:not(.sc-type-h2), h1.widgetCustomizationContent__title {
    color: #06C !important;
    text-shadow: 0 1px #38F;
    position: relative}
.g-modal-title-h1 {
    color: #DDD !important;
    text-shadow: 0 1px #;
    position: relative}
#app h1:not(.sc-type-h2):after, h1.widgetCustomizationContent__title:after, .g-modal-title-h1:after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: linear-gradient(rgba(0,0,0,0) 40%, rgba(0,0,0,.8))}
#app h1.userNetworkTop__title:after, #app h1.groupNetworkTop__title:after, #app h1.listenNetworkTop__title:after, .g-modal-title-h1:after, #app div.l-front h1:after, #app h1.uploadMain__title:after {background: none}
#app h1.userNetworkTop__title, #app h1.groupNetworkTop__title, #app h1.listenNetworkTop__title, .g-modal-title-h1 {-webkit-mask-image: linear-gradient(#000 40%, rgba(0,0,0,.2)) !important}
#app h1.stream__noticeTitle:after {background: linear-gradient(rgba(17,17,17,0) 40%, #111)}
h1.widgetCustomizationContent__title:after, .g-modal-title-h1:after {background: linear-gradient(rgba(21,21,21,0) 25%, #151515)}
*/
    #app h1[class$="Main__title"],
    #app h1 span,
    h1.sc-type-h1 {
        -webkit-mask-image: -webkit-linear-gradient(#000 55%, rgba(0, 0, 0, .2)) !important
    }
    .userDropbar__title,
    h2.selectionModule__titleText,
    .infoStats__value,
    .statsBadge__value,
    .chartTrack__position,
    .collectionSection__heading,
    .insightsSidebarModule__plays {
        /* background: linear-gradient(#1AF, #028) !important;
    background-clip: text !important;
    -webkit-text-fill-color: transparent !important; */
        -webkit-mask-image: -webkit-linear-gradient(#000 35%, rgba(0, 0, 0, .2)) !important;
        /* mask: url(data:image/svg+xml;base64,#1) !important; */
    }
    .header__logo {
        background: linear-gradient(#F50 40%, #510) !important;
        box-shadow: inset 0 0 1px #111, inset 1px 0 #111, inset -1px 0 #111 !important;
    }
    #app h1.header__logo {
        mask: none !important
    }
    #app h1,
    #app h1 *,
    .userDropbar__title,
    h2.selectionModule__titleText {
        color: #06C !important;
        text-shadow: 0 1px #38F !important
    }
    #app h1 a:hover,
    .userDropbar__title:hover {
        color: #18F !important;
        text-shadow: 0 1px #4AF, 0 0 6px #18F !important
    }
    .insightsSidebarModule__plays {
        text-shadow: 0 1px 0 #FFF !important
    }


    .fullHero__title .soundTitle__title,
    .listen-content .soundTitle__title span {
        text-shadow: 0 0 10px #000 !important;
        font-family: "Champagne & Limousines", "eurofurence light", "Interstate", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Garuda, Verdana, Tahoma, sans-serif !important
    }
    .waveform__emptyMessage,
    .waveform__playHint {
        color: #EEE !important;
        text-shadow: 0 0 5px !important
    }

    a {
        text-shadow: 0 0 #000;
        transition: 0.2s color, text-shadow
    }
    .sc-link-dark:hover,
    .sc-link-light:hover,
    .trackPreviewItem__titleLink:hover {
        text-shadow: 0 0 7px
    }

    ::-moz-selection {
        background-color: rgba(35, 35, 35, .7) !important;
        color: #F60 !important;
        text-shadow: 0 1px #000 !important
    }
    ::selection {
        background-color: rgba(35, 35, 35, .7) !important;
        color: #F60 !important;
        text-shadow: 0 1px #000 !important
    }
    :focus {
        outline: none !important
    }

    .sc-border-light-top,
    .sc-border-dark-top,
    .sc-border-light-bottom,
    .sc-border-dark-bottom,
    .sc-border-light-right,
    .sc-border-dark,
    .sidebarHeader,
    .commentsThread__item,
    .ownActivity__user,
    .userInfo,
    .userInfo__container,
    .archive-title,
    .page-title,
    .widget-title,
    .entry-content th,
    .comment-content th,
    .widget-area .widget,
    .sc-border-light,
    .newMessageForm.compact,
    .l-listen-wrapper .l-about-rows,
    .searchTitle,
    .selectionModule {
        border-color: #111 !important
    }
    .g-tabs,
    .g-tabs-item.active,
    .site-content article,
    .commentItem.large.isReply,
    .listenInfoTeaser__separator,
    .collection.m-overview .collection__section,
    .conversation__form,
    .chartsMain_listHeader,
    .sc-border-light-left {
        border-color: #333 !important
    }
    .activeUpload__sectionHead,
    .listenInfoModal__heading,
    .composeMessage__bottomWrapper,
    .g-modal-title-h1 {
        border-color: #222 !important;
        box-shadow: 0 1px #000 !important
    }


    /*** GRAYSCALE STYLE

.waveform:not(:hover), .sc-button-play:not(:hover), a.sound__coverArt > .image, .compactTrackListItem:not(.active):not(:hover):not(:focus) .sc-artwork {
    filter: grayscale(1) !important;
    transform: translate3d(0,0,0) !important;
    transition: filter 0.6s !important;
    -webkit-filter: grayscale(1) !important;
    -webkit-transform: translate3d(0,0,0) !important;
    -webkit-transition: -webkit-filter 0.6s !important}
.header__logo {background: transparent !important}

.soundList__item:hover a.sound__coverArt > .image {
    filter: grayscale(0) !important;
    -webkit-filter: grayscale(0) !important}

***/
    /*** BUTTONS & CO ***/
    .sc-button:not(.sc-button-play),
    .sc-buylink,
    .categoriesButton,
    .murkupdatestart {
        -moz-appearance: none !important;
        -webkit-appearance: none !important;
        background-color: #111 !important;
        border: 1px solid #292929 !important;
        box-shadow: inset 0 0 2px #000 !important;
        color: #777 !important;
        text-shadow: 0 1px #000 !important;
        border-radius: 0 !important;
        transition: color, border-color, background-color, ease 0.3s !important
    }
    .sc-button.blockButton,
    .sc-button.blockButton:active,
    .hintButton.sc-button-small,
    .hintButton.sc-button-small:active {
        background-color: transparent !important;
        border: none !important;
        box-shadow: none !important
    }
    .sc-button.sc-button-selected,
    .sc-button.uploadButton.sc-button-selected {
        border-color: #0066CC !important
    }
    .sc-buylink,
    .sc-button-small.sc-button-text {
        padding: 2px 7px !important
    }
    .sc-buylink:before {
        margin-left: 5px !important
    }
    .sc-buylink:before,
    .sc-button-medium:not(.sc-button-selected):before {
        opacity: 0.4
    }
    .sc-buylink {
        margin-bottom: -4px !important
    }

    .sc-button:hover,
    .sc-buylink:hover,
    .categoriesButton:hover,
    .murkupdatestart:hover {
        border-color: #444 !important;
        color: #999 !important
    }
    .sc-button:active,
    .sc-buylink:active,
    .categoriesButton:active,
    .murkupdatestart:active {
        background-color: #333 !important;
        transition-duration: 0.1s !important
    }
    .sc-button[disabled="disabled"] {
        opacity: 0.5 !important
    }
    .sc-button-play {
        background: #ff5500 !important;
        border: 1px solid #Ffff !important;
        box-shadow: 0 0 5px #000 !important;
        transition: ease 0.3s !important
    }
    .sc-button-play:before {
        margin-top: -1px !important
    }
    .sc-button-play:hover {
        border-color: #fff !important;
        box-shadow: 0 0 8px #fff !important
    }
    .soundTitle.single .soundTitle__playButton .sc-button-large {
        margin-top: 6px !important
    }

    .sc-toggle {
        background: linear-gradient(#222, #111) !important;
        border: 2px solid #191919 !important
    }
    .sc-toggle:before {
        box-shadow: inset 0 0 2px #000 !important
    }
    .sc-toggle-handle {
        background: #111 !important;
        box-shadow: inset 0 0 2px #000 !important
    }

    input,
    input[type="checkbox"],
    input[type="radio"],
    textarea,
    .sc-input,
    input.widgetCustomization__colorInput {
        -moz-appearance: none !important;
        -webkit-appearance: none !important;
        background: #222 !important;
        border: 1px solid #292929 !important;
        box-shadow: inset 0 0 2px #000 !important;
        color: #777 !important;
        text-shadow: 0 1px #000 !important;
        border-radius: 0 !important;
        transition: color, border-color, background-color, ease 0.3s !important
    }
    input[type="checkbox"],
    input[type="radio"] {
        min-width: 12px !important;
        min-height: 12px !important
    }
    input[type="radio"] {
        border-radius: 20px !important
    }
    input[type="checkbox"]:active:hover,
    input[type="radio"]:active:hover {
        background: #222 !important;
        border: 1px solid #292929 !important;
        color: #F70 !important
    }
    .tokenInput__input {
        border: none !important
    }

    input:hover,
    input:focus,
    .sc-input:hover,
    .sc-input:focus,
    textarea:hover,
    textarea:focus {
        background: #333 !important
    }
    input:focus,
    .sc-input:focus,
    textarea:focus {
        color: #AAA !important
    }
    .commentForm .commentForm__input {
        border-color: #333 !important
    }

    .sc-button.uploadButton {
        background: transparent !important;
        border-color: #1C1C1C !important;
        border-radius: 5px !important;
        box-shadow: 0 1px 0 #3E3E3E, inset 0 1px 0 #464646 !important;
        padding-top: 1px !important;
        height: 25px !important
    }
    .sc-button.uploadButton:hover {
        box-shadow: 0 1px 0 #3E3E3E, inset 0 0 3px #090909 !important
    }

    .radioGroup__active,
    .activeUpload__licenseSummary {
        background-color: #222 !important
    }

    a.shuffleButton {
        background-position: 0 0 !important;
        color: #DDD !important;
        opacity: 0.3
    }
    a.shuffleButton:hover {
        opacity: 0.6 !important
    }
    a.shuffleButton.active {
        opacity: 1
    }

    input[type="checkbox"]:checked,
    input[type="radio"]:checked {
        border-color: #F80 !important
    }
    /* select {
  padding-right: 12px !important;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAKCAYAAABrGwT5AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAApSURBVChTYxjiYObMmf+hTBSASxwDoCskWiMMwDSQrBEGyNY4FAADAwDdpBOHzViE2AAAAABJRU5ErkJggg==) rgba(35,35,35,.7) no-repeat right center !important} */
    /*** DIALOG, POPUP & CO ***/
    .dialog,
    .imageLink.zoom,
    .modal__modal,
    #ghostery-purple-bubble,
    .callout__bubble {
        background: #151515 !important;
        border: 1px solid #333 !important;
        border-radius: 0 !important;
        box-shadow: 0 0 10px #000, 0 0 10px #000 !important
    }
    .dialog__arrow,
    .callout__bubble::before {
        background: #151515 !important;
        border-color: #3C3C3C !important;
        border-radius: 0 !important;
    }
    .callout__bubble::before {
        border-bottom: 1px solid #3C3C3C !important;
        border-right: 1px solid #3C3C3C !important;
        padding: 7px !important;
    }
    #ghostery-purple-bubble * {
        background: transparent !important
    }

    .g-selectable {
        background: linear-gradient(#191919, #141414) !important;
        color: #999 !important
    }
    .g-selectable:hover {
        background: linear-gradient(#222, #1B1B1B) !important
    }
    .g-selectable.g-selected {
        background: transparent !important;
        color: #555 !important
    }

    .dropdownList {
        background: #151515 !important;
        border-color: #333 !important
    }
    .dropdownMenu,
    #ui-datepicker-div,
    .repostOverlay__container {
        background: #1C1C1C !important;
        border: 1px solid #292929 !important;
        box-shadow: 0 1px 1px #000 !important;
        text-shadow: 0 1px #000 !important
    }
    .dropdownMenu button,
    .moreActions__button,
    .moreActions__link,
    .headerMenu__link {
        color: #999 !important
    }
    .dropdownMenu > li:hover,
    .dropdownMenu button:hover,
    .dropdownMenu li.selected,
    .moreActions__button:hover,
    .moreActions__link:hover,
    .headerMenu__link:hover,
    .notificationBadge__link:hover {
        background-color: rgba(35, 35, 35, .6) !important;
        color: #F60 !important;
        text-shadow: 0 1px 0 #000 !important
    }
    .notificationBadge--unread {
        background: linear-gradient(rgba(80, 60, 50, .4), rgba(5, 40, 40, .4)) !important
    }
    .headerMenu__link:active,
    .headerMenu__link:focus,
    .notificationBadge__link:active,
    .notificationBadge__link:focus {
        background-color: rgba(70, 70, 70, .6) !important
    }
    #suggestionsList li:hover * {
        color: #F60 !important
    }
    #suggestionsList li > * > * > * > * {
        color: #555 !important
    }
    .dropdownMenu li.separator,
    div.headerMenu ul.headerMenu__list,
    .dropdownContent__main,
    .dropdownContent__header,
    .dropdownContent__listItem {
        border-bottom: 1px solid #292929 !important
    }
    .dropdownContent__listItem *,
    .moreActions__group:not(:first-child) {
        border-color: #292929 !important
    }
    /* .dropdownMenu li,*/
    .dropdownMenu ul.sc-list-nostyle,
    .dropdownMenu > div,
    .dropdownContent__container,
    .headerMenu.m-light,
    .moreActions__button,
    .moreActions__link {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important
    }

    .userNav__button.selected:before {
        border-bottom-color: #F60 !important
    }
    .userNav__button.selected:after {
        border-bottom-color: #000 !important
    }

    .gritter-item-wrapper,
    .activeUpload__hqNote {
        background: linear-gradient(#282828, #1C1C1C) !important;
        color: #CCC !important;
        text-shadow: 0 1px #000 !important;
        border-color: #333 !important;
        box-shadow: 0 0 10px #000, 0 0 10px #000, inset 0 0 3px #111 !important
    }
    .gritter-image {
        top: 0 !important;
        left: 0 !important
    }

    .ui-datepicker .ui-state-default {
        color: #777;
        background-color: #111
    }
    .ui-datepicker .ui-state-default.ui-state-highlight {
        color: #000;
        background-color: #999
    }
    .ui-datepicker .ui-state-default.ui-state-active {
        color: #FFF;
        background-color: #F60
    }
    .ui-datepicker .ui-state-default.ui-state-hover:not(.ui-state-highlight):not(.ui-state-active) {
        color: #CCC;
        background-color: #000
    }
    .ui-datepicker .ui-datepicker-unselectable .ui-state-default,
    .ui-datepicker .ui-state-disabled .ui-state-default {
        color: #555;
        background-color: transparent
    }

    .modal {
        background: repeating-linear-gradient(-45deg, rgba(11, 11, 11, .8), rgba(11, 11, 11, .8) 60px, rgba(16, 16, 16, .8) 60px, rgba(16, 16, 16, .8) 120px) !important
    }

    /* .dialog > .dialog__content > .userRichBadge > .userRichBadge__avatar > .userAvatarBadge.userRichBadge__avatar.sc-media-image.letterbox */
    .userRichBadge .userAvatarBadge__avatar {
        margin: 0 !important
    }
    /* .dialog.sc-border-box.g-z-index-overlay {padding-bottom: 0 !important} */
    /* Soundcloud Stream Manager by Mr Brainz */
    #murkconsole {
        height: 280px !important
    }
    #murkconsole,
    #murkconsole > div {
        background: #000 !important;
        border-color: #111 !important
    }
    #murkconsole p,
    #murkconsole p input {
        line-height: 14px !important;
        vertical-align: top !important
    }


    /*** BOTTOM BAR PLAYER ***/
    .playControls__inner,
    .playControls__bg {
        background: #1c1c1c !important;
        border: none !important
    }
    .playbackTimeline__duration {
        color: #999 !important;
    }
    .playbackTimeline__progressBackground {
        background: #999 !important
    }
    .playbackTimeline__progressBackground,
    .playbackTimeline__progressBar {
        box-shadow: 0 0 2px #FFFFFF !important;
        height: 6px !important;
        border-radius: 3px !important;
        margin-top: -3px !important
    }
    .playbackTimeline__progressHandle {
        box-shadow: 0 0 2px #101010, inset 0 0 2px #1c1c1c !important;
        margin-left: -4px !important;
        margin-top: -4px !important
    }
    .playControls .playControls__icon,
    .playControls .volume__speakerIcon,
    button.playControls__control,
    .playControls__control > button,
    .playbackSoundBadge__showQueue {
        filter: invert(66.5%) hue-rotate(180deg) !important;
        -webkit-filter: invert(66.5%) hue-rotate(180deg) !important;
        transition: 0.3s !important
    }
    .playControls .playControls__icon:hover,
    .playControls .volume__speakerIcon:hover,
    button.playControls__control:hover,
    .playControls__control > button:hover,
    .playbackSoundBadge__showQueue:hover {
        filter: invert(100%) hue-rotate(180deg) !important;
        -webkit-filter: invert(100%) hue-rotate(180deg) !important
    }
    .shuffleControl.m-shuffling,
    .repeatControl.m-one,
