    /* videos text color */
    body *
    {
        color: #2B3693 !important;
        font-family: 'Poppins', Roboto, Arial, sans-serif !important;
    }

    body::after
    {
        position: fixed;
        bottom: 0;
        right: 0;
        z-index: 1;
        content: "";
        font-size: 60px;
        width: 500px;
        height: 377px;
        background-repeat: no-repeat;
        background-size: 500px 400px;
        background-image: url(https://i.imgur.com/pPMUGRj.png);
    }

    .ytd-watch-flexy
    {
        z-index: 1;
    }

    .html5-video-player:not(.ytp-transparent), .html5-video-player.unstarted-mode, .html5-video-player.ad-showing, .html5-video-player.ended-mode, .html5-video-player.ytp-fullscreen
    {
        z-index: 1;
    }

    video-stream html5-main-video
    {
    }
    /*HEADER*/
    ytd-masthead,ytd-browse
    {
        background-color: #FDE6E0 !important;
    }

    #search-icon-legacy.ytd-searchbox
    {
        border-radius: 0px 200px 200px 0px;
        background: #5BAF97 !important;
        border: #5BAF97 1px solid !important;
        box-shadow: none !important;
    }

    #right-arrow-button.ytd-feed-filter-chip-bar-renderer,#right-arrow-button.ytd-feed-filter-chip-bar-renderer,#left-arrow-button.ytd-feed-filter-chip-bar-renderer,#left-arrow-button.yt-chip-cloud-renderer,#right-arrow-button.yt-chip-cloud-renderer,#left-arrow.yt-chip-cloud-renderer:after, #right-arrow.yt-chip-cloud-renderer:before
    {
        background: none;
        background-color: transparent !important;
    }

    /* ytd-masthead .ytd-topbar-logo-renderer yt-icon
     {
       background: url() !important;
       width: 100px !important;
       ;
           background-size: contain !important;
       ;
           background-repeat: no-repeat !important;
       ;
     }

     ytd-masthead .ytd-topbar-logo-renderer yt-icon svg
     {
       display: none !important;
       ;
     }*/
    /*sidebar*/
    #guide-inner-content
    {
        background-color: rgba(43, 54, 147, 0.8) !important;
    }

    #guide-inner-content *
    {
        color: #F9B49F !important;
    }

    ytd-mini-guide-renderer,ytd-mini-guide-entry-renderer
    {
        background-color: rgba(43, 54, 147, 0.8) !important;
        color: #F9B49F !important;
    }

    ytd-mini-guide-entry-renderer[system-icons] .title.ytd-mini-guide-entry-renderer
    {
        color: #FDE6E0 !important;
    }

    ytd-mini-guide-entry-renderer[is-active] .title.ytd-mini-guide-entry-renderer
    {
        color: #F9B49F !important;
    }

    ytd-mini-guide-entry-renderer svg
    {
        stroke: #F9B49F;
    }

    ytd-mini-guide-entry-renderer[is-active] svg
    {
        fill: #F9B49F;
        stroke: #F9B49F;
    }

    /*top filters*/
    iron-selector
    {
        background-color: #FDE6E0;
    }

    chip-bar-renderer
    {
        background: none !important;
        background-color: none!important;
    }

    #right-arrow.ytd-feed-filter-chip-bar-renderer:before,#left-arrow.ytd-feed-filter-chip-bar-renderer:after
    {
        background: none !important;
    }
    /* yt-chip-cloud-chip-renderer.ytd-feed-filter-chip-bar-renderer
     {
       border: 1px solid #0a639f;
       background: transparent;
       color: #0a639f;
     }
   */
    yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER],yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT]
    {
        border: none !important;
        border-radius: 20px;
        background-color: #F9B49F !important;
    }

    yt-formatted-string[ellipsis-truncate]
    {
        color: #2B3693 !important;
    }

    yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT][selected],yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER][selected]
    {
        border: 2px solid #2B3693 !important;
        /*blue*/
        background: #F9B49F !important;
        transform: rotate(5deg);
    }

    /* main content */
    ytd-rich-grid-renderer,ytd-section-list-renderer,ytd-watch-flexy
    {
        background-color: #FDE6E0 !important;
        background-position: right bottom;
        background-repeat: no-repeat;
        background-size: 500px 400px;
        background-attachment: fixed;
    }
    /*right banner playlist*/
    #container.style-scope.ytd-playlist-panel-renderer
    {
        background-color: #FDE6E0;
    }

    ytd-app
    {
        background-color: #FDE6E0;
    }

    #media-hover-overlay
    {
        border-radius: 10px;
    }

    #media-container
    {
        border-radius: 10px;
    }

    yt-image img
    {
        border-radius: 10px;
    }

    img.yt-img-shadow
    {
        border-radius: 10px !important;
    }

    #container.ytd-masthead
    {
        /*background-color: trasparent;*/;
    }

    .yt-simple-endpoint,ytd-thumbnail-overlay-bottom-panel-renderer
    {
        border-radius: 10px !important;
    }

    img.yt-img-shadow
    {
        border-radius: 10px !important;
    }
    /*SEARCH*/
    #container.ytd-searchbox
    {
        border-radius: 200px 0px 0px 200px;
        background: #5BAF97 !important;
        border: #5BAF97 1px solid !important;
        box-shadow: none !important;
        margin-left: 10px;
    }

    .ytd-searchbox::placeholder
    {
        color: #2B3693 !important;
        margin-left: 10px !important;
    }

    ytd-button-renderer #button.ytd-button-renderer
    {
        border-radius: 200px;
        background: transparent;  /* unneccesary*/
    }

    #search-form #search-input{
        margin-left: 10px !important;
    }
    /*Progess*/

    .html5-load-progress,.ytp-load-progress
    {
        background: #F9B49F !important;
    }

    .ytp-swatch-background-color
    {
        background: #F9B49F;
    }

    .html5-scrubber-button, .ytp-scrubber-button.ytp-swatch-background-color
    {
        width: 40px !important;
        height: 40px !important;
        border: none !important;
        margin-left: 0px !important;
        margin-top: -20px;
        margin-bottom: 0px !important;
        transform: scale(0.8);
        background-image: url(https://i.imgur.com/3qWz9fh.gif) !important;
        background-repeat: no-repeat;
        background-attachment: fixed;
        background-position: center;
        background-size: cover;
        border-radius: 50px !important;
        background-color: transparent !important;
    }
}
