css:
@-moz-document domain("youtube.com")
{
    /* videos text color */
    body * {
        color: #0a639f !important
    }

    yt-chip-cloud-chip-renderer.ytd-feed-filter-chip-bar-renderer
    {
        border: 1px solid #0a639f;
        background: transparent;
        color: #0a639f;
    }
    yt-chip-cloud-chip-renderer.ytd-feed-filter-chip-bar-renderer.iron-selected {
        background-color: #eb5b24 !important;
    }
    /*HEADER*/
    ytd-masthead {
        background-color: #eb5b24 !important;
    }

    ytd-masthead .ytd-topbar-logo-renderer yt-icon {
        background: url(https://i.imgur.com/lvaOh7A.png) !important;
        width: 100px !important;
        background-size: contain !important;;
        background-repeat: no-repeat !important;;
    }
    ytd-masthead .ytd-topbar-logo-renderer yt-icon svg {
        display: none !important;;
    }
    /*sidebar*/
    #guide-inner-content {
        background-color: #0a639f;
    }
    #guide-inner-content * {
        color: #fdd41e !important;
    }

    /*top filters*/
    iron-selector {
        background-color: #fdd41e;
    }

    /* main content */
    ytd-rich-grid-renderer {
        background-color: #fdd41e;
    }
    ytd-watch-flexy {
        background-color: #fdd41e;
    }
    #right-arrow-button {
        background-color: #fdd41e !important;
    }
    #right-arrow.ytd-feed-filter-chip-bar-renderer:before {
        background: linear-gradient(to left,#fdd41e 20%,rgba(253, 212, 30,0) 80%) !important;
    }
    #left-arrow-button {
        background-color: #fdd41e !important;
    }
    #left-arrow.ytd-feed-filter-chip-bar-renderer:after {
        background: linear-gradient(to right,#fdd41e 20%,rgba(253, 212, 30,0) 80%)
    }
    ytd-mini-guide-renderer {
        background-color: #0a639f;
    }
    a.ytd-mini-guide-entry-renderer{
        background-color: #0a639f !important;
    }
    ytd-mini-guide-entry-renderer * {
        color: #fdd41e !important;
    }
    #chips-wrapper {
        border-color: #0a639f !important;
    }
}
