css:
@-moz-document domain("roblox.com")
{
    iframe {
        display: none !important;
    }
    .game-carousel .grid-item-container .game-card-link .game-card-thumb-container,
    .list-item.hover-game-tile .featured-game-icon-container .thumbnail-2d-container.brief-game-icon,
    .item-card-container .item-card-link .item-card-thumb-container,
    .item-card-container .item-card-link .item-card-thumb-container thumbnail-2d .thumbnail-2d-container
    {
        border-radius: 100px !important;
    }
    .game-carousel .grid-item-container .game-card-link .game-card-thumb-container:hover,
    .list-item.hover-game-tile .featured-game-icon-container .thumbnail-2d-container.brief-game-icon:hover,
    .item-card-container .item-card-link .item-card-thumb-container:hover,
    .item-card-container .item-card-link .item-card-thumb-container thumbnail-2d .thumbnail-2d-container:hover
    {
        border-radius: 8px !important;
    }
    @keyframes bgcolor {
        0% {
            background-color: #FF7B7B
        }

        30% {
            background-color: #8987FF
        }

        60% {
            background-color: #79C7FF
        }

        90% {
            background-color: #FDFF9E
        }

        100% {
            background-color: #FF7B7B
        }
    }

    body, .content {
        -webkit-animation: bgcolor 60s infinite !important;
        animation: bgcolor 60s infinite !important;
        -webkit-animation-direction: alternate !important;
        animation-direction: alternate !important;
    }

    @keyframes color {
        0% {
            color: #FF7B7B
        }

        30% {
            color: #8987FF
        }

        60% {
            color: #79C7FF
        }

        90% {
            color: #FDFF9E
        }

        100% {
            color: #FF7B7B
        }
    }
    #header, #navigation {
        background-color: black;
    }
    #header *,  #navigation * {
        -webkit-animation: color 60s infinite !important;
        animation: color 60s infinite !important;
        -webkit-animation-direction: alternate !important;
        animation-direction: alternate !important;
    }
    #header .icon-nav-menu, #header .navbar-brand .icon-logo-r, #header #nav-notifications, #header #nav-robux, #header #nav-settings{
        background-color: white;
        border-radius: 8px;
    }
}
