css:
@-moz-document regexp(".*google\.com\/?.*")
{
    /*background image*/
    body
    {
        background-image: url("https://wallpaperaccess.com/full/1202064.jpg");
        min-height: 0px;
        background-position: top;
        background-repeat: repeat-y;
    }
    /*google logo image*/
    .k1zIA.rSk4se
    {
        background: url(https://i.imgur.com/FELoQUJ.png);
        width: 200px;
        height: 100px;
        background-size: contain;
        background-repeat: no-repeat;
    }

    .k1zIA.rSk4se img.lnXdpd
    {
        display: none;
    }

    .sfbg, .yg51vc
    {
        background: transparent !important;
    }

    .GLcBOb
    {
        border: none !important;
    }

    .g, .ULSxyf
    {
        background: white !important;
    }
    /*bottom buttons*/
    input[type="submit"]
    {
        border: 3px solid #fdd41e !important;
        background: white !important;
        color: #0a639f !important;
    }
    /* search input wrapper*/
    .RNNXgb
    {
        background-color: #fdd41e !important;
        color: #0a639f !important;
        border: 3px solid #0a639f;
    }

    /* search auto complete colors */
    .mkHrUc * {
        color: #0a639f !important;
    }

    /*search text color*/
    .RNNXgb input
    {
        color: #0a639f !important;
    }
    /*search magnifier icon color*/
    .RNNXgb svg path
    {
        color: #0a639f !important;
    }
    .o3j99.c93Gbe {
        background: transparent !important;
    }
    .o3j99.c93Gbe .uU7dJb{
        border: none !important;
    }

    /* footer start */
    .o3j99.c93Gbe {
        background: transparent !important;
    }
    .o3j99.c93Gbe .uU7dJb{
        border: none !important;
    }
    /* footer end  */

    /* top right icon color */
    svg.gb_Ue {
        color: #fdd41e !important;
    }
}

@-moz-document regexp(".*google\.com\/search.*")
{
    body
    {
        background-image: unset;
    }
    /*top left logo*/
    #logo
    {
        background: url("https://ntvb.tmsimg.com/assets/p18722572_b_h8_ak.jpg") 50% 80% no-repeat !important;
        background-size: contain !important;
        padding-bottom: 80px !important;
        bottom: 70px !important;
    }

    .logo.doodle img
    {
        background: url(https://pics.filmaffinity.com/The_Simpsons_TV_Series-170785177-large.jpg) 100% 100% no-repeat !important;
        background-attachment: fixed !important;
        background-size: 300px !important;
    }

    /*bottom right image*/
    .main
    {
        background-image: url(https://cdn.invictawatch.com/www/img/drops/the-simpsons/simpsons_family.png) !important;
        background-attachment: fixed !important;
        background-size: 300px;
        background-repeat: no-repeat;
        background-position-x: right;
        background-position-y: bottom;
    }
    /*search result links*/
    .hdtb-mitem a
    {
        background: white !important;
        border: 1px solid #fdd41e !important;
        color: #0a639f;
    }

    /*search result cubes*/
    .hdtb-mitem.hdtb-msel
    {
        background: #fdd41e !important;
        border: 1px solid white !important;
        color: #0a639f;
    }
}
