css:
@-moz-document domain("google.com")
{
    body {
        background-image: url(https://i.imgur.com/HVXm2PX.jpg)
    }
    body * {
        color: #FFE81F !important;
        text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;

    }

    /*hide google logo*/
    .k1zIA.rSk4se img.lnXdpd
    {
        display: none;
    }

    /*google logo image*/
    .k1zIA.rSk4se
    {
        background: url(https://i.imgur.com/YzzZfva.png);
        width: 200px;
        height: 100px;
        background-size: contain;
        background-repeat: no-repeat;
        transform: scale(2.5);
    }

    .o3j99.c93Gbe, .o3j99.c93Gbe *
, .o3j99.ikrT4e.om7nvf *
    {
        background: transparent !important;
        border-color: #ffe81f;
    }
     .o3j99.ikrT4e.om7nvf .erkvQe {
        background: black !important;
          background-color: black !important
    }
}

@-moz-document regexp(".*google\.com\/search.*")
{
    #rcnt.GyAeWb {
        justify-content: center;
        transform: perspective(400px) rotateX(26deg);
        height: calc(100vh - 350px);
        /* border: 1px solid red; */
        overflow: scroll;
        max-width: 100%;
        position: fixed;
        width: 1000px;
        bottom: 332px;
        left: calc((100% - 1000px) / 2);
    }
    #rcnt.GyAeWb::-webkit-scrollbar {
        display: none;
    }
    #center_col.s6JM6d {
        margin-top: calc(100% - 500px);
    }

    body, .CvDJxb .sfbg, #pTwnEc, #extabar
    , .g.Ww4FFb.vt6azd.tF2Cxc, #appbar
    {
        background: transparent;
    }
    #stylish-main-extension-slider-7f78697
    , #stylish-main-extension-slider-7f78697 *
    {
        background: unset !important;

    }
    body {
        background-image: url(https://i.imgur.com/HVXm2PX.jpg)
    }
    body *{

        color: #FFE81F !important;
        text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
    }
    .RNNXgb, .aajZCb {
        background: black !important;
        border: 1px solid #ffe81f;
    }

}
