/* i really want this to be global */

.box-content {
    background-image: url(https://cdn.discordapp.com/attachments/488395042889924609/767731724473729056/file-20180619-126537-5l632j.png)
   }

  a.btn-outline-main:hover
  {
    color: #00e7ff;
  }

  .btn-outline-main:not(:disabled):not(.disabled).active,
.btn-outline-dark:not(:disabled):not(.disabled):active,
.show > .btn-outline-main.dropdown-toggle
  {
    background-color: #fff;
    border-color: #278a0f;
  }

  .btn-outline-main:not(:disabled):not(.disabled).active,
.btn-outline-dark:not(:disabled):not(.disabled):active,
.show > .btn-outline-main.dropdown-toggle
  {
    color: #0547ff;
  }

  a,
.btn-link
  {
    color: #00e7ff;
  }

/* Notificare Noua! */
  a.notification-message:hover
  {
    color: #24bb00;
  }

  a:hover
  {
    color: #fff;
  }

/*Asta e pt pagina de creat banere, de a se intelege scrisul de acolo care nu se vedea */
  .margin-bottom-double.gray-container
  {
    color: #a01800;
  }

/* width */
  ::-webkit-scrollbar
  {
    width: auto;
  }

/* Track */
  ::-webkit-scrollbar-track
  {
    box-shadow: inset 0 0 10px #3a010b;
    border-radius: 10px;
  }

/* Handle */
  ::-webkit-scrollbar-thumb
  {
    background: #fff;
    border-radius: 10px;
  }

/* Handle on hover */
  ::-webkit-scrollbar-thumb:hover
  {
    background: #00ffff;
  }

  #thank-episode.btn.btn-primary:hover
  {
    color: #2bff00;
  }

  a.notification-message.seen-message
  {
    color: #00ffff;
  }

/* Notificare Deschisa */
  a.notification-message.seen-message:hover
  {
    color: #0078ff;
  }


