css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* CONTAINER WIDTHS */

#blueBar {
    visibility: hidden !important;
}

#globalContainer {
    width: 796px !important;
}

#leftCol, .uiSideNav li {
    width: 149px !important;
}

#leftCol {
    background: #F7F7F7 !important;
}

.UIRequestBox > div {
    padding-bottom: 4px !important;
    border-bottom: 1px solid #DDD !important;
}

#pagelet_status_updater {
    display: none !important;
}

.uiSideNav .item, .uiSideNav .subitem {
    border-color: transparent !important;
}

#pageHead {
    height: 34px !important;
}

#contentCol {
    margin-left: 151px !important;
    width: 646px !important;
    padding-top: 0 !important;
}

#contentArea {
    border: solid #B3B3B3 !important;
    border-width: 0 1px 1px 0 !important;
    padding-bottom: 17px !important;
}

.UIIntentionalStream_Content h3 {
    font-size: 11px !important;
}

#bottomContent {
    display: none !important;
}

#rightCol {
    width: 184px !important;
    border: solid #CCC !important;
    border-width: 1px 1px 1px 0 !important;
    background: #F7F7F7 !important;
    padding: 5px !important;
    height: 100% !important;
}

#pagelet_eventbox input.event_name {
    width: 176px !important;
}

#pagelet_chat_home {
    display: none !important;
}

.hasLeftCol #contentCurve {
    visibility: hidden !important;
}

#rightCol .UITitledBox_Top {
    background: #E9E9E9 !important;
    border: none !important;
    padding: 5px !important;
}

#rightCol .UITitledBox_Content {
    padding: 5px !important;
}

.UIHomeBox_MoreLink {
    text-transform: lowercase !important;
}

/* NAVBAR */

#pageLogo a, a.fb_logo_link {
    padding: 0 !important;
    height: 55px !important;
    width: 151px !important;
    left: 0 !important;
    top: 0 !important;
    background: url(data:image/png;base64,) !important;
}

#pageLogo a:hover, a.fb_logo_link:hover {
    background: url(data:image/png;base64,) !important;
}

a.fb_logo_link img {
    display: none !important;
}

#jewelCase {
    bottom: auto !important;
    top: 0 !important;
    left: 334px !important;
    z-index: 98 !important;
    width: auto !important;
}

#jewelCase .jewel {
    border: none !important;
    margin: 0 !important;
}

#requestsWrapper {
    display: none !important;
}

#jewelMail {
    display: none !important;
}

#jewelBoxMail {
    display: block !important;
    visibility: hidden !important;
    top: 0 !important;
    border: none !important;
}

#jewelBoxMail .jewelFooter a {
    visibility: visible !important;
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
    border: none !important;
    padding: 12px 10px 0 !important;
    height: 22px !important;
    background: transparent !important;
}

#jewelBoxMail .jewelFooter a:hover {
    background: #6D86B7 !important;
}

#jewelCase .jewelFooter a:first-child {
    font-size: 0 !important;
    line-height: normal !important;
}

#jewelCase .jewelFooter a:first-child:before {
    font-size: 11px !important;
    content: "Inbox ";
    color: #FFF !important;
}

#jewelOuterInboxCount {
    padding: 0 !important;
    font-size: 0 !important;
}

#jewelInnerInboxCount {
    font-size: 11px !important;
    color: white !important;
}

#jewelInnerInboxCount:before {
    content: "(";
}

#jewelInnerInboxCount:after {
    content: ")";
}

#jewelCase .jewelUnread .jewelFooter .seeMoreCount {
    display: inline !important;
}

#navSearch {
    left: auto !important;
    right: 6px !important;
    top: 7px !important;
    width: 188px !important;
}

#navSearch .inputtext {
    width: 159px !important;
}

#pageNav {
    left: 0 !important;
    right: auto !important;
}

#pageNav > li > a {
    padding: 12px 10px 0 !important;
}

#headNavOut {
    top: 0 !important;
    margin-left: 151px !important;
    border: none !important;
    width: 614px !important;
    height: 34px !important;
    border-bottom: 1px solid #254588 !important;
    background: #3B5998 !important;
}



/* NOTIFICATIONS */

.fbDockWrapper {
    margin-right: 32px !important;
}

#notificationsWrapper {
    position: fixed !important;
    bottom: 0 !important;
    right: 16px !important;
    border: none !important;
    height: 25px !important;
    width: 32px !important;
}

#jewelBoxAlert {
    position: fixed !important;
    bottom: 24px !important;
    right: 16px !important;
    left: auto !important;
    top: auto !important;
    border-bottom-width: 1px !important;
    border-color: #999 #999 #FFF #999 !important;
    z-index: 99 !important;
    width: 230px !important;
}

#jewelCase .jewelHeader {
    background: #526EA6 !important;
    margin: 0 0 22px !important;
    padding: 3px 8px !important;
    font-size: 11px !important;
}

#jewelCase .jewelHeader h3 {
    color: white !important;
}

#jewelBoxAlert li {
    padding: 0 7px !important;
}

#jewelBoxAlert li > div > div {
    border-top: 1px solid #CCC !important;
    padding: 7px 0 !important;
}

#jewelNotifs i.img {
    margin-top: 10px !important;
}

#jewelBoxAlert li.selected {
    border: none !important;
}

#jewelAlert {
    background: url(data:image/png;base64,) !important;
    height: 25px !important;
    width: 32px !important;
}

#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active {
    background: url(data:image/png;base64,) !important;
}

.jewelNew .jewelCount {
    background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAARCAYAAAA7bUf6AAAAVklEQVR42mNgAILXDAz/ycUMMAOArP9/czJIxv9hBsEMIRePGjJqCH0NYTDKxcgP6Irw5h2gfnD+ATOgGNmQZZqa/5HlsGJs4DW6AeSA15QagOI1EgAAmNzkT69pqOcAAAAASUVORK5CYII=) no-repeat !important;
    width: 18px !important;
    height: 16px !important;
    right: 1px !important;
    top: -1px !important;
}

.jewelNew .jewelCount span {
    border: none !important;
    font-weight: normal !important;
    padding: 1px 3px !important;
    background: transparent !important;
}

#jewelBoxAlert .jewelFooter a {
    position: absolute !important;
    top: 26px !important;
    right: 7px !important;
    background: transparent !important;
    border: none !important;
    font-weight: normal !important;
    font-size: 0 !important;
    padding: 0 !important;
}

#jewelBoxAlert .jewelFooter a:after {
    font-size: 11px !important;
    content: "See All";
}

#jewelBoxAlert .jewelFooter a:hover:after {
    text-decoration: underline !important;
    color: #3B5998 !important;
}

/* PRESENCE BAR */

#presence_ui {
    margin: 0 !important;
}

body #presence {
    right: 46px !important;
}

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab {
    background: #E5E5E5 !important;
}


#presence.fbx_bar .presence_section.focused, #presence.fbx_bar .presence_section:hover, #presence.fbx_bar #chat .tab_handle.focused, #presence.fbx_bar #chat .tab_handle:hover, #presence.fbx_bar #presence_gk_tab.focused, #presence.fbx_bar #presence_gk_tab:hover, #presence.fbx_bar #presence_translations_tab.focused, #presence.fbx_bar #presence_translations_tab:hover {
    background: #FFF !important;
}

}
