css:
@-moz-document domain("novelmtl.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#explore ul.proplist li a,

.chapter-content,
.pagination>li>a, .pagination>li>span,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.chapter-content
{
	font-size: 1.85rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.google-auto-placed,
.navbar-menu li #nightmode,
.ob-smartfeed-wrapper.feedIdx-0,
.user-menu li #nightmode,

ins.adsbygoogle.adsbygoogle-noablate
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.novel-header .novel-info
{
	padding: 1.2rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.content-wrap
{
	max-width: none!important;
}

.container,
.content-wrap,
.main-header .wrapper,

footer .wrapper
{
	width: 98%!important;
}

}

@-moz-document domain("novelmtl.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.novel-header .novel-info .author span,
.novel-item .novel-stats .status.cpl,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.control-action .font-select:before,
.navigation-bar,
.novel-item .novel-stats span,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.novel-tab-links a:hover span,
.novel-tab-links a._on span,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

#chapters .heading .intro strong,

.novel-header .novel-info .author span:first-child,
.novel-header .novel-info .header-stats span small,
.novel-item .novel-stats,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.novel-item .novel-stats span:nth-child(2),

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("novelmtl.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

a figure::before {
    background: linear-gradient(to right,#f3b8b81a 0%,#e6cbd01a 10%,#cdd4f003 100%);
}

/* Background 
_____________________________________________________________________________________________________*/

.main-header,

footer
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#chapters .chapter-list li:nth-child(4n), #chapters .chapter-list li:nth-child(4n-1),
#chapters .heading .intro,
#explore nav.filters,
#novel,
#latest-updates .novel-list.grid.col.chapters .novel-item,

.chapter-content,
.chapter-header,
.chapternav,
.control-action .font-select .font-wrap label,
.control-action .font-select:before,
.novel-cover,
.novel-header::before,
.pagination>li>a, .pagination>li>span
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.chapternav a,
.login-wrap-nav .login,
.novel-header .novel-info .links a
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.chapternav a::before,
.login-wrap-nav .login::before,
.novel-header .novel-info .links a::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.chapternav a:hover::before,
.login-wrap-nav .login:hover::before,
.novel-header .novel-info .links a:hover::before
 {
  opacity: 1!important;
}

/* Select 
_____________________________________________________________________________________________________*/

.control-action .action-items .action-select a.chnav
{	
	background: radial-gradient(circle, #2d73e7a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.control-action .action-items .action-select a.chnav::before
{
	background: radial-gradient(circle, #000 0%, #2d73e7 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.control-action .action-items .action-select a.chnav:hover::before {
  opacity: 1!important;
}

/* Purple
_____________________________________________________________________________________________________*/

.novel-header .novel-info .links a.empty,
.section-header .getmorebtn
{	
	background: radial-gradient(circle, #5856d6a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	z-index: 1!important;
}

.novel-header .novel-info .links a.empty::before,
.section-header .getmorebtn::before
{
	background: radial-gradient(circle, #000 0%, #5856d6 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.novel-header .novel-info .links a.empty:hover::before,
.section-header .getmorebtn:hover::before {
  opacity: 1!important;
}

/* Gray 
_____________________________________________________________________________________________________*/

.novel-header .novel-info .categories ul li,

a.isDisabled 
{	
	background: radial-gradient(circle, #6a6b6ca8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.novel-header .novel-info .categories ul li::before,

a.isDisabled ::before
{
	background: radial-gradient(circle, #000 0%, #6a6b6c 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.novel-header .novel-info .categories ul li:hover::before,

a.isDisabled :hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.control-action .font-select .font-wrap,
.control-action .font-select
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.control-action .font-select .font-wrap:hover,
.control-action .font-select:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

#chapters .chapter-list li:hover,

.control-action .font-select .font-wrap label:hover,
.control-action .font-select .font-wrap input[type="radio"]:checked+label
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.novel-item .novel-stats .status.cpl
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

.novel-item .novel-stats .status
{
	background: radial-gradient(ellipse at center, #1b8bc8 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

#explore ul.proplist li a.active,
.pagination>li.active>a, .pagination>li.active>span
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

.novel-header .novel-info
{
	background: rgb(2,0,36);
	background: linear-gradient(22deg, rgb(0, 0, 0) 0%, rgba(7, 6, 6, .49) 35%);
}
}

@-moz-document domain("novelmtl.com") {
/* Image Settings */

#chapters .chapter-list li,

.novel-item,

p
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

#chapters .chapter-list li:hover,

.novel-item:hover,

p:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

selector:hover
 {
	opacity: 1!important;
}

}

@-moz-document domain("novelmtl.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

.fas {
    margin-top: 0;
    flex-direction: row;
    justify-content: flex-start;
    font-size: 27px;
    font-weight: 600;
}

a[title="Explore All Novel Tags"] .fas
{
	font-size: 3rem!important;
}

/* Categories ___________________________________________________________________________*/

.fa-list-alt:before {
    content: "";
}

.fa-list-alt:before
{
	background: url(https://i.ibb.co/pnNpGSt/lines.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-list-alt:before
{
	background-size: 25px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Login ___________________________________________________________________________*/

.fa-sign-in-alt:before {
    content: "";
}

.fa-sign-in-alt:before
{
	background: url(https://i.ibb.co/fpGB5C8/login.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-sign-in-alt:before
{
	background-size: 25px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Logo ___________________________________________________________________________*/

.nav-logo {
    width: 19rem;
}

[alt="Light Novels"],
[alt="logo-footer"]
{
	background: url(https://i.ibb.co/1Lp4dFB/raspberry.png) no-repeat!important;
	box-sizing: border-box;
}

[alt="Light Novels"],
[alt="logo-footer"] {
    background-size: 18rem!important;
    padding-left: 19rem!important;
    width: 1px!important;
}

/* Notification ________________________________________________________________________*/

.fa-bell:before {
    content: "";
}

.fa-bell:before {
	background: url(https://i.ibb.co/8Px680k/Notification.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-bell:before {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}


/* Search ___________________________________________________________________________*/

.fa-search:before {
    content: "";
}

.fa-search:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-search:before
{
	background-size: 25px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Tags ___________________________________________________________________________*/

.fa-tags:before {
    content: "";
}

.navbar-menu li a[title="Explore All Novel Tags"] i
{
    transform: rotate(45deg)!important;
}

.fa-tags:before
{
	background: url(https://i.ibb.co/Cn6xnZ4/tag-yellow.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-tags:before
{
	background-size: 20px!important;
	padding-left: 21px!important;
	width: 1px!important;
	transform: rotate(45deg)!important;
}

/* Updates ______________________________________________________________________________*/

.fa-flag-checkered:before {
    content: "";
}

.fa-flag-checkered:before
{
	background: url(https://i.ibb.co/2nRDcZd/flag-red2.png) no-repeat!important;
	box-sizing: border-box;
}

[title="Novels updated with new chapters"]:hover .fa-flag-checkered:before
{
	background: url(https://i.ibb.co/Hrpm5JM/Finish-Flag.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-flag-checkered:before,
[title="Novels updated with new chapters"]:hover .fa-flag-checkered:before
{
    background-size: 25px!important;
    padding-left: 26px!important;
    width: 10px!important;
}


}

@-moz-document domain("novelmtl.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
