@-moz-document domain("devhints.io") {
/* you can change the arrow's color by editing the 'rgb()' element nestled in the url. It's a bit tricky to spot and you can't delete the '%2C's between the rgb numbrs, but hey it's better than nothing lol */

.h3-section.-intro > .body > ul > li > a::before
/* svg arrow when not hovering on link */
{
  background:url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22512%22%20height%3D%22512%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20fill%3D%22rgb(200%2C200%2C200)%22%20d%3D%22M85%20277.375h259.704L225.002%20397.077%20256%20427l171-171L256%2085l-29.922%2029.924%20118.626%20119.7H85v42.75z%22%2F%3E%3C%2Fsvg%3E") center center/24px 24px no-repeat;
}

.h3-section.-intro > .body > ul > li:hover > a::before
/* arrow when hovering on link */
{
  background:url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22512%22%20height%3D%22512%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20fill%3D%22rgb(125%2C125%2C125)%22%20d%3D%22M85%20277.375h259.704L225.002%20397.077%20256%20427l171-171L256%2085l-29.922%2029.924%20118.626%20119.7H85v42.75z%22%2F%3E%3C%2Fsvg%3E") center center/24px 24px no-repeat;
}

.missing-message.missing-message > h3::before
/* arrpw near homepage's missing message */
{
  background: url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22512%22%20height%3D%22512%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20fill%3D%22rgb(125%2C125%2C125)%22%20d%3D%22M85%20277.375h259.704L225.002%20397.077%20256%20427l171-171L256%2085l-29.922%2029.924%20118.626%20119.7H85v42.75z%22%2F%3E%3C%2Fsvg%3E") center center/24px 24px no-repeat;
}

html,
body,
.h3-section > .body,
.h3-section > .body > h4,
.h3-section > .body > p,
.h3-section.-prime > .body,
.MarkdownBody pre,
.search-footer,
.related-posts-area,
.MarkdownBody pre.-setup,
.MarkdownBody p.-setup,
.MarkdownBody ul.-setup,
.MarkdownBody p.-crosslink
/* sets the background for the entire page */
{
  background: #262529;
	box-shadow: 1px 1px 2px #1a1a1a;
}

body
/* main header */
{
  color: #d2dfe6;
}

.site-header > h1
/* homepage main header */
{
  color: #eff2f5;
}

.site-header > p
/* homepage sub-header */
{
  color: #abb3ba;
}

.missing-message.missing-message > h3
/* homepage missing message */
{
  color: #abb3ba;
}

.main-heading > .h1 > em
/* main header alternate text */
{
  color: #797f83;
}

.MarkdownBody table td:first-child>code
/* colored markdown code */
{
  color: #878cbf;
}

.MarkdownBody pre>code
/* plain markdown code */
{
  color: #d2dfe6;
}

.MarkdownBody code
/* colored code converted to plain code for specific sections of markdown */
{
  color: #d2dfe6;
}

.token.keyword
/* keywords */
{
  color: #b4769d;
}

.token.value,
.token.string,
.token.number,
.token.attr-value,
.token.boolean,
.token.regex
/* values, strings, numbers, attribute-values, booleans, and regex */
{
  color: #87bf8d;
}

.token.function,
.token.attr-name
/* functions and attribute-names */
{
  color: #8a6fa6;
}

.token.punctuation,
.token.operator
/* operators and punctuation */
{
  color: #df96c1;
}

.token.comment
/* comments */
{
  color: #65696b;
}

a
/* links */
{
  color: #84558a;
}

a:visited
/* visited links */
{
  color: #84558a;
}

a:hover
/* links that are being hovered over */
{
  color: #ae74b5;
}

.MarkdownBody h2:target
/* main headers that have been focused (i.e: clicked on) */
{
  color: #f5ee89;
}

.MarkdownBody h3
/* markdown titles */
{
  color: #d7d5e3;
}

.MarkdownBody
/* main color of markdown text (and for some reason also the main headers??) */
{
  color: #d5dde3;
}

.MarkdownBody a,
.MarkdownBody a:visited
/* links within the markdown areas (most notably in the "Introduction" section) */
{
  color: #84558a;
}

.MarkdownBody em
/* I don't fully know but my guess is that these are alternate fonts for the plain markdown text (or subtext within the plain markdown text, lol). My reasoning behind this is that the only element that uses this color (at least on the page I was using to make this style) is one bit of text that's nestled inside some parenthesis*/
{
  color: #aeb4b9;
}

.MarkdownBody .local-anchor,
.MarkdownBody .local-anchor:visited
/* the '#' part of big headers (will be referred to as the "header anchor") */
{
  color: #d5dde3;
}

.MarkdownBody .local-anchor:target,
.MarkdownBody :target>.local-anchor
/* clicked header anchors */
{
  color: #f5ee89;
}

.MarkdownBody .local-anchor:hover,
.MarkdownBody .local-anchor:focus
/* hovered header anchors */
{
  color: #e0e7ec;
  background-color: rgba(253, 254, 255, 0.1);
}

.h3-section > .body p a,
.h3-section > .body p a:visited
/* links in markdown footers/headers */
{
  color: #84558a;
}

.h3-section > .body p
/* markdown footers/headers (generic text only, as code is still colored here) */
{
  color: #d5dde3;
}

.search-box
/* search box in footer */
{
  background-color: #57555e;
}

.search-box > .prefix
/* little box inside of search box */
{
  background-color: #6f6d78;
}

.search-box > .prefix,
.search-box > .sep,
.search-box > input,
.search-box > input::placeholder
/* search box text */
{
  color: #e6e6e5;
}

.related-post-item > a,
.related-post-item > a:visited
/* footer items */
{
  background: #534c67;
  color: #b3afb6;
}

.related-post-item > a > strong,
.related-post-item > a:visited > strong
/* footer items header */
{
  color: #cdadeb;
}

.related-post-item:first-of-type > a,
.related-post-item:first-of-type > a:visited
/* first footer item in list */
{
  background-color: #94499c;
  color: #e8e6e3;
}

.pages-list > .article > .info > .slug
/* homepage text fix */
{
  color: #c2cdd6;
}

.pages-list > .article:visited > .info > .slug
/* homepage visited text fix */
{
    color: #818d98;
}

.pages-list > .article:hover > .info > .title,
.pages-list > .article:focus > .info > .title
/* homepage hover text fix */
{
  color: #a281c6;
}
}
