css:
@-moz-document domain(myshows.me) {

.comment-rating.negative:before {
    width: 10px;
    height: 2px;
    background: #ccc;
    display: block;
    margin: auto;
    content:" ";
}

.comment-rating.negative {
    font-size: 0;
}

.comment-actions__vote.comment-actions__vote--positive_voted + .comment-rating.negative:before {
	display: none;
}

.comment-actions__vote.comment-actions__vote--positive_voted + .comment-rating.negative:after {
	content:"+1";
    font-size: 16px;
    color: #709401;
}

.comment-rating.comment-rating--placeholder {
    color: transparent !important;
}

.Sidebar-container a[href="/profile/movies/"] {
    display: none;
}

.UserDetails-rating {
    display: none;
}

}
