css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("derstandard.at") {
body {
  margin: 0 auto !important;
}
#pageTop {

}
#Sitebar {
  display: none !important;
}
#toolbar {
position: fixed;
left: auto !important;
margin: 0 auto !important;
display: block;
text-align: right;
min-width: 975px !important;
width: 975px !important;
background: transparent !important;

}
#toolbar ul#toolbarMenu {
  display: block;

}
#toolbar ul#toolbarMenu a {
  color: #333 !important;
  background: #fff;
  opacity: 0.7;
}
#toolbar ul#toolbarMenu a.button:hover {
  opacity: 0.9;
}
#toolbar #TBUser {
  width: auto !important;
  display: block;
  background-color: #efefef;
  color: #333 !important;
}
#toolbar #TBUser a, #TBLoginName, #toolbarMenu .flyout li a {
  color: #333 !important;
}
#toolbar #TBUser a {
  color: #333 !important;
  opacity: 0.5;
}
#toolbar #TBUser a:hover {
  color: #333 !important;
  opacity: 0.9;
}
#weiterLesen  {
  margin-left: 756px !important;
  left: auto !important;
}
#toolbarMenu .flyout, #toolbarMenu .flyout div.wrapper, #toolbarMenu .flyout div.wrapper p ul li a,  #toolbarMenu .flyout div.wrapper p ul li, #toolbarMenu a, #toolbarMenu p, #toolbarMenu p ul li, #toolbarMenu p em,  #toolbarMenu b,  #toolbarMenu strong {
  background-image: none !important;
  background-color: #fff !important;
  color: #333 !important;
  border: 0;
  opacity: 0.9;
}
#toolbarMenu p ul li a:hover {
  opacity: 1 !important;
  color: #000 !important;
}
#toolbarMenu .flyout ul, #TBServicesMenu {
  border-bottom: 0 !important;
}

}
