css:
@-moz-document domain("learncpp.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#access a>span
{
	text-transform: capitalize!important;
}

.cpp-lightbluebackground,
.cpp-lightgraybackground,
.cpp-lightgreenbackground,
.cpp-lightredbackground,

:focus,
pre
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

code[class*=language-],
li code,
pre,
pre[class*=language-]
{
	font-size: 1.85rem!important;
}

body,
p
{
	font-size: 1.52rem!important;
}

#main pre[class*=language-], #main pre[class*=language-] *
{
	line-height: 2.5rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#container.three-columns-sided #secondary,

.cf_monitor,
.ezmob-footer-desktop,

[title*="dark mode"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#branding .identity+#site-text
{
	margin: 1rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.changetable-row-action
{
	padding: 1.2rem!important;
}

.chaptertable-row-number,
.lessontable-row-number,

li code,
p code
{
	padding: 3px 30px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

#site-header-main {
    height: 84px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

#breadcrumbs-container-inside,
#colophon-inside,
#container.three-columns-sided .main,
#footer-inside,
#site-header-main-inside,

.lessontable, .changetable,
.three-columns-sided #breadcrumbs,

body:not(.septera-landing-page) #container
{
	max-width: none!important;
}

.changetable-row-action,
.changetable-row-date,
.chaptertable-row-number,
.lessontable-row-number
{
	width: auto!important;
}

#primary {
    width: 18%!important;
}
}

@-moz-document domain("learncpp.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	

	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("learncpp.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label,
li code,
p code
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#comments,
#site-description,

body,
code[class*=language-], pre[class*=language-],
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.cpp-definition,
.lessontable-header-title,

h1,
h2,
p.cpp-note-title
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.cpp-section.cpp-topline,

em,
h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.changetable-row-date,
.changetable-widget-row-date,

time
 {
	color: #07beff!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #000!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}


}

@-moz-document domain("learncpp.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

#colophon,
#footer,
#site-header-main
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#lp-page,
#lp-posts,

.changetable-row,
.changetable-row:nth-child(odd),
.chaptertable-row,
.lessontable-row,
.lessontable-row:nth-child(odd),
.searchform,

article.page table td, article.post table td,
body:not(.septera-landing-page) .main
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

button,
input[type=button],
input[type=submit],
input[type=reset]
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

button::before,
input[type=button]::before,
input[type=submit]::before,
input[type=reset]::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

button:hover::before,
input[type=button]:hover::before,
input[type=submit]:hover::before,
input[type=reset]:hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.changetable-widget-row
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#main .cpp-table,
#primary,

.lessontable
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#main .cpp-table:hover,
#primary:hover,

.changetable-widget-row:hover,
.lessontable:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.cpp-lightredbackground
{
	background: linear-gradient(to bottom, #590505 0%, #000 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

#main .cpp-table th,

.chaptertable-row-number,
.lessontable-row-number,
.changetable-row-action,
.cpp-lightbluebackground,
.lessontable-header-chapter
{
	background: linear-gradient(to bottom, #6daaf3, #000 100%)!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.cpp-lightgreenbackground,

[style*="background:#6ff"]
{
	background: linear-gradient(to bottom, #015001 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.cpp-lightgraybackground
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.changetable-row-action,
.chaptertable-row-number,
.cpp-lightbluebackground,
.cpp-lightgraybackground,
.cpp-lightgreenbackground,
.cpp-lightredbackground,
.lessontable-header-chapter,
.lessontable-row-number,

[style*="background:#6ff"],
pre
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Code 
_____________________________________________________________________________________________________*/

li code,
p code,
pre
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.changetable-row:hover,
.changetable-row:nth-child(odd):hover,
.chaptertable-row:hover,
.lessontable-row:hover,
.lessontable-row:nth-child(odd):hover,
.main tr:not(:first-child):hover,

li:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

}

@-moz-document domain("learncpp.com") {
/* Image Settings */

.changetable-row,
.changetable-row:nth-child(odd),
.chaptertable-row,
.lessontable-row,
.lessontable-row:nth-child(odd),
.main tr:not(:first-child),

li,
p
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.changetable-row:hover,
.changetable-row:nth-child(odd):hover,
.chaptertable-row:hover,
.lessontable-row:hover,
.lessontable-row:nth-child(odd):hover,
.main tr:not(:first-child):hover,

li:hover,
p:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("learncpp.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

.fa {
    font-size: 26px!important;
}

/* Arrows _______________________________________________________________________*/

.fa-chevron-circle-left:before,
.fa-chevron-circle-right:before {
    content: "";
}

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

.fa-chevron-circle-left:before
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.fa-chevron-circle-right:before
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-chevron-circle-left:before,
.fa-chevron-circle-right:before
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

/* Home _____________________________________________________________________________*/

.fa-home:before {
    content: "";
}

.fa-home:before
 {
    background: url(https://i.ibb.co/c63D58g/Home4.png) no-repeat!important;
    box-sizing: border-box;
}

.fa-home:before
 {
    background-size: 35px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

.fa.fa-home {
    font-size: 46px!important;
}

/* Latest Changes ___________________________________________________________________________*/

.fa-list:before {
    content: "";
}

.fa-list:before
{
	background: url(https://i.ibb.co/M2CMhys/Line-Numbers.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-list:before
{
	background-size: 25px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.icon-search:before {
    content: "";
}

.icon-search:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-search:before
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Site Index ___________________________________________________________________________*/

.fa-crosshairs:before {
    content: "";
}

.fa-crosshairs:before
{
	background: url(https://i.ibb.co/q7B97jC/Crosshair.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-crosshairs:before
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}


}

@-moz-document domain("learncpp.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
