css:
@-moz-document url-prefix(https://www.wanikani.com/lesson/session),
                   url-prefix(https://www.wanikani.com/review/session) {
#loading, #loading-screen {
  background-color: #000 !important;
}

body, .footer-adjustment, #reviews-summary, .wall-of-shame, html#main .navbar.scrolled .navbar-inner, html#main .navbar.navbar-static-top, .navbar-inner, section#progress, h2.progression, .additional-info, .alternative-meaning {
  background-color: #000 !important;
  background-image: none !important;
}

.span12 .page-list ul {
  background-color: #030303 !important;
  border-color: #202020 !important;
}
  
.navbar .container, .forum-post-author, #supplemental-info {
  background-color: #030303 !important;
  box-shadow: 0px 0px 0px 000 !important;
}
  
#search-form input, .page-list ul, #top-members, #new-members, #chat ul a, .progression{
  background-color: #111111 !important;
}
  
#active-topics tbody, .forum-post-body, #created-topics tbody, .forum-post-body, .forum-topics-list tbody {
  background-color: #090909 !important;
}
  
#active-topics tbody:nth-child(2n-1), #created-topics tbody:nth-child(2n-1), .forum-topics-list tbody:nth-child(2n-1) {
  background-color: #101010 !important;
}
  
#active-topics tbody tr:hover, #created-topics tbody tr:hover, .forum-topics-list tbody tr:hover {
  background-color: #252525 !important;
}
  
.recent-unlocks h3, .see-more, .low-percentage h3, .recent-retired h3 {
  background-color: #252525 !important;
}
  
.recent-unlocks tr, .low-percentage tr, .recent-retired tr {
  background-color: #303030 !important;
}
  
.threshold {
  color: #000 !important;
}
  
.forum-post-author div {
  margin-left: -30px !important;
}

#correct, #incorrect {
  background-color: #151515 !important;
  box-shadow: none !important;
}

.forum-header-bg, .forum-header-bg::before, header::before {
  background-color: #252525 !important;
}

#reviews-summary #review-stats #review-stats-radicals, #chat ul a, .progression, #reviews-summary #review-stats #review-stats-kanji, .progression, #reviews-summary #review-stats #review-stats-vocabulary {
  background-color: #101010 !important;
  box-shadow: none !important;
  color: #999 !important;
}

#reviews-summary #review-stats #review-stats-answered-correctly {
  box-shadow: none !important;
}

#lessons-summary #vocabulary, #lessons-summary #kanji, #lessons-summary #radicals {
  box-shadow: none !important;
}

#lessons-summary #vocabulary div, #lessons-summary #kanji div, #lessons-summary #radicals div {
  background-color: #151515 !important;
}

#lessons-summary #vocabulary div ul li, #lessons-summary #kanji div ul li, #lessons-summary #radicals div ul li, #correct div ul li, #incorrect div ul li {
  box-shadow: none !important;
}

#correct div h3, #incorrect div h3 {
  border-bottom: 2px solid #060606 !important;
}

#correct div h3 span, #incorrect div h3 span {
  background-color: #151515 !important;
  color: #FFF !important;
}

#correct div h3 span strong, #incorrect div h3 span strong {
  background-color: #363636 !important;
  color: #FFF !important;
}

.page-list ul li a, .page-list ul li.page-list-header {
  background-color: #252525 !important;
  border-color: #202020 !important;
}
.page-list ul li.active a {
  background-color: #181818 !important;
}

[id*="level-"] header h2 {
  background-color: #111 !important;
}

#chat.forum-list ul a li::before {
  background-color: #252525 !important;
}

.span6 h3.invert {
  background-color: #252525 !important;
  background-image: none !important;
}

.span6 table tbody tr:not(.see-more) {
  background-color: #151515 !important;
}

#user-response, #answer-submit, #answer-form button {
  background-color: #333 !important;
  box-shadow: 0px 0px 0px #353535 !important;
}

#information, #information blockquote, #supplement-info, #supplement-kan-meaning blockquote, #supplement-kan-reading blockquote {
  background-color: #151515 !important;
  color: #FFF !important;
  box-shadow: 0px 0px 0px #151515 !important;
}
.user-synonyms-add-btn::before, #all-info {
  background-color: #363636 !important;
  color: #FFF !important;
}
#additional-content ul li span {
  background-color: #252525 !important;
  box-shadow: 0px 0px 0px #252525 !important;
}
a[href*="/kanji/"] div, a[href*="/radicals/"] div, a[href*="/vocabulary/"] div, a[href*="/kanji/"], a[href*="/radicals/"], a[href*="/vocabulary/"] {
  color: #FFF !important;
}

.highlight-vocabulary {
  background-color: #A000F1 !important;
}
.highlight-kanji {
  background-color: #F100A0 !important;
}
.highlight-radical {
  background-color: #0AF !important;
}
.highlight-reading {
  background-color: #454545 !important;
}

#batch-items ul li span {
  box-shadow: 0px 0px 0px #000 !important;
}
[data-index="quiz"] span {
  background-color: #333 !important;
}

#report-errors a, #report-errors button {
    border-radius: 3px 3px 0 0;
    transition: color .15s linear;
    background-color: #e1e1e1;
    border: none;
    color: #888;
    visibility: hidden;
}

#reviews footer #hotkeys {
    cursor: pointer;
    display: inline-block;
    padding: 7.5px 9px;
    color: #888888;
    font-size: 14px;
    vertical-align: bottom;
    margin-right: 10px;
    visibility: hidden;
}

#question #question-type.meaning {
  background: #888 !important;
  color: #252525 !important;
  text-shadow: none;
  border-top: 1px solid #888;
  border-bottom: 1px solid #888;
}

#question #question-type.reading {
  background: #252525 !important;
  color: #888 !important;
  text-shadow: none;
  border-top: 1px solid #252525;
  border-bottom: 1px solid #252525;
}

#answer-form input[type=text] {
  color:  beige
}

#question #character {
    color: #252525;
}
#stats {
  color: #252525
}

#summary-button {
 color: #252525
}

#question #character.kanji {
    background-color: #847;
    background-image: none;
    text-shadow: none;
}

#question #character.vocabulary {
    background-color: #637;
    background-image: none;
    text-shadow: none;
}

#question #character.radical {
    background-color: #369;
    background-image: none;
    text-shadow: none;
}
  
#progress-bar #bar {
    background-color: #999;
}  
}
