css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tacticalgamer.com") {
.threadbit .pagination .pagelinks a:hover{
color:#333 !important;
}
.threadbit .pagination .pagelinks a {
-moz-border-radius:1px !important;
background-color:#eee !important;
}


.postbitlegacy .postbody, .eventbit .eventdetails .eventbody{
background-color:#cfcfcf !important;
color:#cfcfcf !important;
border-left:1px solid #666 !important;
}

.userinfo,.postdetails{
background-color:#8e8e8e !important;
}
.postbody{

}

.threadbit .pagination .pagelinks{
border:none !important;
}
#bd{
background-color:#fff !important;
padding-left:10px !important;
margin-top:10px !important;
margin-right:30px !important;
-moz-border-radius: 10px !important;
}

li .threadbit {
color:#ff9000 !important;
}

.title{

}
.article_author_date_comment_container{
color:#979797 !important;
}

#comments{
color:#555 !important;
}

 .forumbit_post,.floatcontainer,.blockrow  {
color:#676767 !important;
}

.announceinfo,.announcement,#threadpagestats{
color:#8e8e8e !important;
}

body{
 background-color:#333;
color:#ccc !important;

}

#vbulletin_html{
 background-color:#333 !important;
}

#pagetitle h1{
color:#fff !important;
text-shadow:#000 0px 1px 0px;
}
#pagetitle p{
color:#ccc !important;
text-shadow:#000 0px 1px 1px;
}

#breadcrumb{
color:#ccc !important;
text-shadow:#000 0px 1px 0px;
}

.breadcrumb .navbit a:hover {
background-color:#222 !important;
-moz-box-shadow:0px 1px 1px #555 !important;
}
.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead{
-moz-box-shadow:2px 2px 2px #000 !important;
}
.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats{
color:#515151 !important;
}
.newcontent_textcontrol{
-moz-box-shadow:0px 2px 5px #111 !important;
}

.pagination a{
background-color:#555 !important;
border:none !important;
color:#888!important;
}
.pagination span{
border:1px solid #666 !important;
}
.pagination span:hover{
border-top:1px solid #999 !important;
}


/*RESET SHADOWS */
.notices li, .footer, .thread_info , .thread_info_block,.threadinfohead,.options_block h4,.threadlisthead,.announcerow{
-moz-box-shadow:0px 2px 5px #111 !important;}

}
