css:
@-moz-document domain("mysyma.symamobile.com") {
#loginpage #login-scroller, #get-password .ui-content, .login-form,
.ui-mobile .ui-page, .top_info_perso, .subpart_title span, .facture_details_title,
.facture_icons_titles, .facture_icons_title_wrapper, .invoice_method_change_wrapper, html
{
	background: #242D34!important;
}
#wallet_part, #forfait_detail_brief_part, #conso_tracking_part,
.inner_page .content_scroller, .browser_menu_main
{
	background: #242D34;
    /*border: 1px solid #4F6171;*/
    color: #938686 !important;
}
.menu_option_active, .optionsWrapper .menu_option:hover, .optionsWrapper .menu_option:active
{
    background:#2D3740 !important;
}
.container ._right
{
	background-color: #2D3740;
	color: transparent;
	border: 1px solid #4F6171;
}

.conso_date_time .conso_date_time_inner_wrapper
{
	background: #242D34 !important;
}
.optionsWrapper .menu_option
{
	border-bottom: none !important;
    border-right: none !important;
}
.container ._right .content .body input, .container ._right .content .body button.submit
{
	background-color: transparent;
	border: none;
	border-bottom: 1px solid #333;
	color: #202022;
}
.login-form .container ._right .content .body input.submit#container_submitButton,
.login-form .container ._right .content .body button.submit#container_submitButton,
.login-form #get_password_submitButton
{
	background: #4F6171!important;
    color: #202022!important;
}
.top_info_perso, .conso_date_time
{
    color:#4F6171!important;
}
.border.grey_circle, circle, .main_footer.footer
{
   visibility: hidden !important; 
}
.wallet_credit_link
{
	border: 1px solid #4F6171;
}
.wallet_credit_link:hover
{
	background: #2D3740;
	border-color: #4F6171;
	color: orange;
}
.optionsWrapper .menu_option:active
{
    color:orange !important;
}
.wallet_amount, .forfait_brief_amount, .my_offer_amount,
.forfait_amount, .pass_amount, .option_amount, .forfait_brief_next_transaction_desc,
.forfait_brief_next_transaction_date,
.forfait_brief_last_bill_date, .forfait_brief_last_bill_download,
.conso_tracking_circle_main_text, #roaming-internet-circle > div:nth-child(2) > div:nth-child(4),
#out_of_forfait-circle > div:nth-child(2) > div:nth-child(3)
{
	color: #808040 !important;
}
#internet-circle > div:nth-child(2) > div:nth-child(4),
.infoperso_email
{
    color: orange !important;
}
.option_activate_button:hover, .option_activate_button:active
{
	background: #2D3740;
	color: orange;
}
.option_activate_button
{
	border: 1px solid #808040;
	border-radius: 6px;
	color: #808040;
	cursor: pointer;
}
.option_details_open, .option_details_close,
.my_offer_details_open, .my_offer_details_close, .secure_data_edit,
.secure_data_close, .forfait_details_open, .forfait_details_close,
.pass_details_open, .pass_details_close
{
	box-shadow: 0 0 0 1px #435361;
	color: #435361;
}
.option_details_open:hover
{
    color: #808040;
}
.facture_amount
{
	color: orange;
}
.syma_red.capitalize
{
	color: #808040;
}
.content_top_title, .horizontal_line_separator,
.payments_bills_details_part,
.facture_list_item
{
	border-bottom: 1px solid #435361 !important;
}
.facture_list_item_icons .icon-icon_pdf_factures.main_icon
{
	border-left: 1px solid #435361 !important;
	border-right: 1px solid #435361 !important;
}
.facture_list_item_icons .icon-icon_pdf_appels.main_icon
{
	border-right: 1px solid #435361 !important;
}
.invoice_method_change_text a, #forfait_brief_last_bill_download
{
    color: #808040!important;
    text-decoration: none!important;
}
.invoice_method_change_text a:hover, #forfait_brief_last_bill_download:hover
{
    color: orange!important;
}

.popUpWrapper.isError .syma-pop-up.generalPopUp
{
	background-color: #2D3740;
    border-bottom: 1px solid #435361;
}
.syma-pop-up.generalPopUp .alert-action
{
    color: orange;
}
.dotted_horizontal_line
{
	border-bottom: 1px solid #435361;
}
/* ==================================== SUPPRESSION ==================== */

.browser_header, footer,
.ui-mobile a img, .ui-mobile fieldset, .login_container_footer,
.container ._left, .showBothButtons, .app_banner,
[data-id="money_transfer"]
{
    display:none!important;
}
}
