css:
/* i really want this to be global */

/* General */

body {
  margin-bottom: 0!important;
}

div#header {
  background: #3E3E3E;
}

div#header,
div#tabs,
div#logo,
div#footer,
#header table td:nth-child(n+3),
button.save-btn:nth-child(1),
form>p:nth-of-type(-n+2) {
  display: none!important;
}

div#tabs {
  position: absolute;
  top: 4px;
}

div#tabs a {
  background: none;
  color: white;
  font-size: 14px;
  font-weight: 600;
}

div#main {
  border-radius: 10px;
  height: calc(100vh - 76px);
  margin-top: 24px;
  width: calc(100% - 48px);
}

#MainBody {
  height: calc(100% - 44px);
}

h2 {
  text-align: left;
}

button.save-btn:nth-child(2) {
  position: absolute;
  bottom: 8px;
  right: 24px;
}

fieldset+p+div>a {
  font-weight: 500;
  position: absolute;
  bottom: 16px;
  left: 24px;
}

form p:first-of-type {
  position: absolute;
  right: 58px;
  top: 100px;
  text-align: right;
}

form p:nth-of-type(2) {
  position: absolute;
  right: 60px;
  top: 146px;
  text-align: right;
}

/* Hide metadata fields and desktop editor */

form>[class^="editor-"]:nth-of-type(-n+37),
fieldset#mobileFields+fieldSet,
fieldset:nth-of-type(n+4),
fieldset#mobileFields>div:nth-child(n+3),
fieldset#desktopTabletFields {
  display: none!important;
}

fieldset#mobileFields {
  height: calc(100% - 72px);
}

fieldset#mobileFields .editor-field {
  margin-bottom: 0!important;
  height: 100%;
}

form#EditBrandVideoFormId,
#DesktopTabletVideoAdHtmlEditor,
#VideoAdHtmlSmallEditor,
#MobileVideoAdHtmlEditor,
.ace_content {
  height: 100%!important;
}

fieldset#mobileFields .editor-label:nth-of-type(1) {
  background: #f46c0f;
  border-radius: 6px;
  color: white;
  font-weight: 600;
  padding: 4px 16px;
  position: absolute;
  left: 50%;
  top: 36px;
  text-align: center;
  transform: translateX(-50%);
}

fieldset#mobileFields .editor-label:nth-of-type(1) label {
  margin-bottom: 0!important;
}

div#MobileVideoAdHtmlEditor {
  width: 100%!important;
}
