css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("philosophyforums.com") {

*{
    
    color: #aaa !important;
    border-color: #000 !important;
}
body{background: #333 none repeat !important;}

TABLE{font-size:14px !important; font-family: Arial !important;}

.quote,.quoteby{font-size:12px !important; font-family: Arial !important;}

Tr, th{background: #151515 none repeat !important;}

td,form{background: #222 none repeat !important;}

input, select, textarea{background: #333 none!important;-moz-appearance: none !important;}

button{background: #333 none!important;-moz-appearance: none !important;border: 1.5px solid #000!important;}

#chatarea,#activechatters{background: #444 none!important;border: 1.5px solid !important; border-color: #111 !important;}

.boxbody,.quotations,.shoutbg,.clear,.footer,#searchbox,.rules,.details,.redirect{background: #222 none!important;}

.boxtitle,.tableheader,.categoryheader,.columnheader,.deletions{background: #151515 none!important;color:#ddd!important;}

.title{color:#ddd!important;}

.quote{background: #333 none!important; border: 1.5px solid !important; border-color: #111 !important;}

.navbutton a,.navbutton a:hover,.homenav, .homenav:link, .homenav:visited,.homenav:hover, .homenav:link:hover, .homenav:visited:hover{background: #222 none!important;border: 1.5px solid !important;border-color: #111 !important}

a.biglink,.biglink:hover{background: #222 none!important;border: 0px solid !important;border-color: #111 !important}

.comment{background: #222 none!important;border-left: 0px solid !important;border-right: 0px solid !important; border-top:1.5px solid!important;border-color: #111 !important;}

a:link, a:visited
{
   color: #ddd !important;
   text-decoration: none !important;
}
a:hover
{
   color: #aaa !important;

}

img[src$="http://forums.philosophyforums.com/templates/images_default/forum_old.png"] {
  width: 0 !important;
  height: 24px !important;
  padding-left: 25px !important;
  background: url("data:image/png;base64,")
  center center no-repeat !important;  }

img[src$="http://forums.philosophyforums.com/templates/images_default/forum_new.png"] {
  width: 0 !important;
  height: 24px !important;
  padding-left: 25px !important;
  background: url("data:image/png;base64,")
  center center no-repeat !important;  }

img[src$="http://forums.philosophyforums.com/templates/images_default/logo.png"] {
  width: 0 !important;
  height: 60px !important;
  padding-left: 231px !important;
  background: url("data:image/jpeg;base64,")
  center center no-repeat !important;  }

}
