css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
 #watch-this-vid #movie_player {
  height:25px !important ;
  width:620px !important ;
}
.vimg90 {
 display:none;
 border:none;
}
.video-thumb-220, .video-thumb-128, .video-thumb-120, .video-thumb-90, .video-thumb-50 {
 display: none !important ;
}
#watch-this-vid-info {
 display:none !important ;
}
#watch-vid-title h1 {
 font-family: Georgia;
 font-weight: normal !important ;
 font-size: 17px !important ;
}
#chrome-promo #chrome-link {
 display: none !important ;
}
.watch-discoverbox-body a {
 font-size: 13px !important ;
}
.mini-list-view .video-main-content {
 width:100% !important;
 margin-bottom:10px !important ;
}
#watch-url-embed-wrapper {
 display:none !important ;
}
.watch-video-desc {
 font-size:11px !important ;
 line-height: 1.5em;
 margin-left: 5px !important ;
}
#subscribeDiv a {
 font-size: 13px !important ;
}
.yt-uix-expander-head {
 font-size: 13px !important ;
}
#watch-promoted-videos-container {
 display:none;
}
.watch-video-added {
 font-size:13px !important ;
}
#watch-longform-popup {
 display:none;
}
#watch-player-div {
 float:left;
}
#watch-vid-title {
 float:left !important ;
}
.user-thumb-medium {
 display:none !important ;
}
#footer {
 display: none !important ;
}
#masthead-search {
 background: none !important ;
 border: none !important ;
 padding: 4px !important ;
}
html > body .yt-button, tt:-moz-any-link, tt:default {
 -moz-border-radius: 0px !important ;
 font-size: 12px !important ;
 background:none !important ;
}
#watch-video-details-inner-more {
 font-size: 11px !important ;
 display:none;
}
#watch-video-details-toggle {
 display:none;
}
#watch-video-details-toggle-more {
 display:none;
}
#watch-channel-vids-top {
 padding:6px !important ;
}
#playlistContainer_PL a {
 font-size: 13px !important ;
}
}
