/* ==UserStyle==
@name           www.allkpop.com - 29/10/2022, 12:27:51 am
@namespace      github.com/openstyles/stylus
@version        2.0.0
@description    Remove Useless Stuff 
@author         Jho
==/UserStyle== */

@-moz-document url-prefix("https://www.allkpop.com/") {

    :root {
        --bg-color: #1e1e2e;
        --fg-color: #11111b;
        --main-color: #EBA0AC;
        --text-color: #d9e0ee;
    }

    body,
    html,
    .grey-bg,
    #article-headline-tags ol li {
        background-color: var(--bg-color) !important;
    }

    nav#main,
    article.list,
    nav#main .vote,
    article.list .vote,
    article.container,
    #f_s_p,
    .cmt_vote,
    #f_s_p .select,
    #f_s_p .select ul,
    #f_s_p .btn,
    #comment-form .select,
    #comment-form .select ul,
    .create_scr .select,
    .create_scr .select ul,
    article.list .title a,
    .redactor-box,
    nav#main ol.main-nav li,
    nav#main ol.main-nav li a,
    .user-nav i,
    #f_s_p .select.active li,
    #comment-form .select.active li,
    .create_scr .select.active li,
    .copyright,
    .comment_focus,
    #search_input_scr,
    .entry_content,
    .entry_content p,
    .entry_content b,
    .entry_content span,
    .entry_content>div,
    .view_from_user_name,
    .view_content,
    .modal-header,
    .modal-body,
    .modal-footer,
    #new_message_panel,
    #new_message_panel .message_to,
    #new_message_panel .message_subject,
    .setting_panel,
    .setting_panel a,
    .report_option {
        background-color: var(--fg-color) !important;
        color: var(--text-color) !important;
    }

    article.list .title a:hover,
    nav#main ol.main-nav li:hover,
    nav#main ol.main-nav li a:hover,
    .article_share:hover,
    .article_edit:hover,
    .article_more:hover,
    .article_report:hover,
    .article_delete:hover,
    #article-headline-tags ol li a:hover {
        color: #d2d2d2 !important;
    }

    .table .tr.odd {
        background-color: #444;
        color: var(--text-color);
    }

    .table .title,
    .draft_cnt_btn,
    .redactor-styles,
    .redactor-styles p,
    .redactor-styles div,
    .redactor-styles span,
    .redactor-styles b,
    .redactor-placeholder:before,
    .np_title a {
        color: var(--text-color);
    }

    .trending_widget,
    .draft_cnt_btn,
    .redactor-box.redactor-styles-on {
        border: 1px solid #d2d2d2;
    }

    .trending_widget article.list,
    .trending_widget article.list .text,
    .anchor_content>article.container {
        background-color: #666 !important;
    }

    .trending_widget article.list .text a,
    .gotop i,
    .gocmt span {
        color: #d2d2d2 !important;
    }

    .gocmt_balloon span,
    .gocmt i,
    #search_input,
    #setting_form #new_email,
    #setting_form #curr_pass,
    #setting_form #u_pass1,
    #setting_form #u_pass2,
    #setting_form .birth_1,
    #setting_form .birth_2,
    #setting_form .birth_3 {
        color: var(--bg-color) !important
    }

    .gocmt,
    .gocmt_balloon {
        background-color: #d2d2d2 !important
    }

    .table .tr.selected,
    .table .tr.selected .td {
        background: #e2e2e2 !important;
    }

    .noti_feed .noti_b,
    .share_this_article_title {
        color: var(--text-color) !important;
    }

    .OUTBRAIN .ob-rec-text,
    .OUTBRAIN .ob-widget-header {
        color: var(--text-color) !important;
    }

    .OUTBRAIN .ob-widget-items-container {
        background-color: transparent !important
    }

    .my_comment {
        background-color: #444 !important;
    }

    .adthrive-sticky.adthrive-footer {
        background-color: hsla(0, 0%, 0%, .8);
        border-top: 2px solid hsla(0, 0%, 50%, .8);
    }

    .menu_list a,
    .usr_setting_scr p a,
    .search_result_title,
    #reset_form h1,
    #reset_form h3 {
        color: var(--text-color) !important;
    }

    .menu_scr,
    .usr_setting_scr {
        background: #222 !important;
        color: var(--text-color) !important;
    }

    .mobile_top_sort_wrap li {
        background-color: #c0c0c0;
    }

    /* Removal */

    .width73,
    #article-headline-left {
        width: 100% !important;
		max-width: 100%!important;
    }

    .home_right, .create_right, #article-headline-right, .external_social_share {
        display: none
    }
	
	.user-nav li:nth-child(1) {
		display: none!important;
	}
	
	.main-nav li:nth-child(4),
	.main-nav li:nth-child(10){
		display: none!important;
	}
	
	a[href*="https://shop.allkpop.com"],
	a[href*="https://forum.allkpop.com/available-subscriptions"] {
		display: none;
	}
	
	.logo-white {
		content: url(https://www.allkpop.com/imgs/placeholder/logo_w.png); 
	}
	
	#f_s_p nav ul li {
		border: 0px!important;
	}
	
	
	/* Tweaks */
	#f_s_p .select, #f_s_p .select ul, #comment-form .select, #comment-form .select ul, .create_scr .select, .create_scr .select ul {
		border: 1px solid #4b4b4b;
	}
	
	#f_s_p .select .arrow-right-wrap, #comment-form .select .arrow-right-wrap, .create_scr .select .arrow-right-wrap {
    	border-left: 1px solid #4b4b4b;
	}
	#f_s_p .select.active li, #comment-form .select.active li, .create_scr .select.active li {
		border: none;
	}
	
	article.container {
		border-bottom: 1px solid #4b4b4b;
		box-shadow: none;
	}
	
	#article-info {
		border-top: 1px solid #4b4b4b;
	}
	
	.prev_article, .next_article, .prev_article, .next_article, #article-headline-tags ol li {
		border: 1px solid #4b4b4b;
	}
	
	.comment-line {
		border-right: 2px solid #4b4b4b!important;
	}
	
	p#comment-form-login-btn {
    	background: #bc0707!important;
	}
	
	/* Ads & Promoted Posts */
	div[id^="promo"],
	div[id^="AdThrive"],
	div[class^="adthrive"],
	.shop_this_story_container,
	.shop_this_story_title,
	.ob-smartfeed-wrapper,
	.adthrive {
		display: none;
	}
	
	
	/* Changelogs 
	
	+ 22 JUN 2023
	--- Removed 'Shop this story'
	
	+ 26 FEB 2023
	--- Removed ads & promoted posts.
	*/
}
