@-moz-document domain("rekietalaw.locals.com") {
    /* Insert code here... */
    
    
  /*  chat comments background */
    
    .pmessage--owner .pmessage__content, .pmessage__content {
	background: #fff;
	border: none;
    }
        
 .theme a {
     font-size: 18px;
        font-weight: 900;
	color: #3fb871;
}
    
    
   .pmessage--creator .pmessage__content .pmessage__text, .pmessage--owner .pmessage__content .pmessage__text, .pmessage__content .pmessage__text {
       font-weight: 900;
	color: #000;
} 
        
    .pmessage__text {
	line-height: 1.4;
	font-size: 18px;
	word-break: break-word;
}
    
    

    .pmessage__ava, .pmessage__avaimg {
	width: 50px;
	height: 50px;
	border-radius: 100%;
}
    
    /* text after who is typing */
        .pnotice {
            font-size: 15px;
            font-weight: 900;
      color: #000;
}
    
    .isLiveChat .pchat #chat-typing {
	margin-top: auto;
	width: 100%;
	position: -webkit-sticky;
	position: sticky;
	bottom: 0;
	left: 0;
	background: #d2d1d1;
}
    

    
}
