css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {

ytd-watch-metadata div#top-row div#owner div#upload-info {
    margin-right: 12px !important;
}

/* channel touroku hihyouji */
ytd-watch-metadata div#top-row div#owner ytd-subscribe-button-renderer button.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled {
    display:none !important;
}

/* member touroku hihyouji */
ytd-watch-metadata div#top-row div#owner div#sponsor-button {
    display:none !important;
}

/* channel mei wo hiyuusen, migino kakusyu aikon wo yuusen */
ytd-watch-metadata div#top-row div#owner {
    min-width: 1px !important;
    flex: 0 1 auto !important;
}
ytd-watch-metadata div#top-row div#actions {
    min-width: 1px !important;
    flex: 1 0 auto !important;
}

/* migigawa icon no setsumei moji sakujo */
ytd-watch-metadata div#top-row div#actions .yt-spec-button-shape-next--tonal.yt-spec-button-shape-next--icon-leading .yt-spec-button-shape-next__icon {
    margin-right: -6px !important;
}
ytd-watch-metadata div#top-row div#actions .yt-spec-button-shape-next--tonal.yt-spec-button-shape-next--icon-leading .yt-spec-button-shape-next__button-text-content {
    display:none !important;
}
ytd-watch-metadata div#top-row div#actions div#segmented-like-button .yt-spec-button-shape-next--tonal.yt-spec-button-shape-next--icon-leading .yt-spec-button-shape-next__icon {
    margin-right: 6px !important;
}/* kouhyouka nomi hyouji */
ytd-watch-metadata div#top-row div#actions div#segmented-like-button .yt-spec-button-shape-next--tonal.yt-spec-button-shape-next--icon-leading .yt-spec-button-shape-next__button-text-content {
    display:initial !important;
}/* kouhyouka nomi hyouji */
ytd-watch-metadata div#top-row div#actions ytd-button-renderer {
    margin-left: initial !important;
}/* icon kankaku kokode reset shite ika de hyouji suru icon dake kankaku wo shitei suru */

