ytd-watch-metadata div#top-row div#actions button[aria-label="再生リストに保存"]{
    margin-left: 8px !important;
}
ytd-watch-metadata div#top-row div#actions button[aria-label="Save to playlist"]{
    margin-left: 8px !important;
}
