style_setting_options.value:
/* analytics hihyouji (jibun no douga no toki) */
ytd-watch-metadata div#top-row div#owner div#analytics-button {
    display:none !important;
}

/* douga no henshuu hihyouji (jibun no douga no toki) */
ytd-watch-metadata div#top-row div#owner div#subscribe-button ytd-button-renderer {
    display:none !important;
}

/* touroku sumi hihyouji */
ytd-watch-metadata div#top-row div#owner div#subscribe-button ytd-subscribe-button-renderer button.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal {
    display:none !important;
}

/* touroku sumi bell hihyouji */
ytd-watch-metadata div#top-row div#owner div#subscribe-button ytd-subscribe-button-renderer div#notification-preference-button{
    display:none !important;
}

/* share hyouji */
ytd-watch-metadata div#top-row div#actions button[aria-label="共有"]{
    margin-left: 8px !important;
}
ytd-watch-metadata div#top-row div#actions button[aria-label="Share"]{
    margin-left: 8px !important;
}

/* share hihyouji */
ytd-watch-metadata div#top-row div#actions button[aria-label="共有"]{
    display:none !important;
}
ytd-watch-metadata div#top-row div#actions button[aria-label="Share"]{
    display:none !important;
}

/* download hyouji */
ytd-watch-metadata div#top-row div#actions ytd-download-button-renderer{
    margin-left: 8px !important;
}

/* download hihyouji */
ytd-watch-metadata div#top-row div#actions ytd-download-button-renderer{
    display:none !important;
}

/* Thanks hyouji */
ytd-watch-metadata div#top-row div#actions button[aria-label="Thanks"]{
    margin-left: 8px !important;
}

/* Thanks hihyouji */
ytd-watch-metadata div#top-row div#actions button[aria-label="Thanks"]{
    display:none !important;
}

/* clip hyouji */
ytd-watch-metadata div#top-row div#actions button[aria-label="クリップ"]{
    margin-left: 8px !important;
}
ytd-watch-metadata div#top-row div#actions button[aria-label="Clip"]{
