css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mvgroup.org") {

*{
    color: #aaa !important;
    border-color: #000 !important;
}

body{background: #333 none repeat !important;}

Tr, th{background: #151515 none repeat !important;}

td{background: #222 none repeat !important;}

input, button, select, textarea{background: #333 none!important;-moz-appearance: none !important;}

#ipbwrapper{background: #333 none!important;}

.pp-tabclear,.pp-maintitle,.nav_mem,.borderwrap h3,.maintitle,.maintitlecollapse,#submenu,
.ipb-top-left-link,.ipb-top-right-link,.ipb-top-left-link-current,.ipb-top-right-link-current,
.member_box,.borderwrap,.formsubtitle,.barc
{background: #151515 none !important;}

.light_button,.dark_botton,.row1,.row2,.row3,span,.popmenubutton
{background: #222 none !important;}


.quotetop,.codetop
{background: #222 none !important;border:0px !important;}

.quotemain,.codemain
{background: #444 none !important;border: 1px solid #000 !important;}

activeusers{border: 1px solid #000 !important;}

p{background: #151515 none !important;}

a:link, a:visited
{
   color: #ddd !important;
   text-decoration: none !important;
}
   
a:hover{color: #aaa !important;}

img[src$="/images/logo1.png"] {
  width: 0 !important;
  height: 59px !important;
  padding-left: 200px !important;
  background: url("data:image/png;base64,")
  center center no-repeat !important;  }


}
