css:
@-moz-document url-prefix("http://www.google.com/search?"),
               url-prefix(http://www.google.com/linux?),
               url-prefix(http://www.google.com/bsd?),
               url-prefix(http://www.google.com/microsoft?) {
/* Yahoo */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > [href*="http://search.yahoo.com/search?"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="search.yahoo.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Ask */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + [href^="http://web.ask.com/web?"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="news.ask.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* AllTheWeb */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + [href^="http://www.alltheweb.com/search?"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="alltheweb.com/search?cat=news"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Live */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + [href*="msn.com"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="search.msn.com/news/results.aspx?q"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Lycos */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + [href^="http://search.lycos.com/default.asp"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="search.lycos.com/default.asp?tab=news&"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Technorati */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + a + [href^="http://www.technorati.com/cosmos/search.html?"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Feedster */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + a + a + [href^="http://www.feedster.com/search"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="feedster.com/search.php?q"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Wikipedia */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + a + a + a + [href^="http://en.wikipedia.org/wiki/Special:Search?"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Bloglines */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + a + a + a + a + [href^="http://www.bloglines.com/search?"]:hover,
[style="margin-top: 3px; font-size: small;"] > [href*="bloglines.com/search?t=1"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* AltaVista */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + a + a + a + a + a + [href^="http://www.altavista.com/web/results?"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* A9 */
[style="padding: 4px 0pt 4px 12px; font-size: small;"] > a + a + a + a + a + a + a + a + a + a + [href^="http://a9.com/"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Sky News */
[style="margin-top: 3px; font-size: small;"] > [href*="search.sky.com/search/skynews/"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* CNN */
[style="margin-top: 3px; font-size: small;"] > [href*="search.cnn.com/pages/search.jsp"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* DayPop */
[style="margin-top: 3px; font-size: small;"] > [href*="daypop.com/search?"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}


[style="padding: 4px 0pt 4px 12px; font-size: small;"]
{padding: 8px 0px 0px 0px !important;} /* top right bottom left */

}
