css:
@-moz-document domain("youtube.com") {
	.html5-video-player {
		border-radius: 10px;
		box-shadow: 0px 7px 20px 3px rgba(0,0,0,0.3);
	}
	
	.html5-video-info-panel, .ytp-popup {
		border-radius: 10px;
		box-shadow: 0px 7px 20px 3px rgba(0,0,0,0.5);
		backdrop-filter: blur(25px) contrast(1.3) saturate(1.3);
		background: rgba(15,15,15,0.65);
	}
}
