@-moz-document url-prefix("http://images.google.com/images?") {

/* Yahoo */
[style="padding: 4px 0pt 4px 8px; font-size: small;"] > [href*="http://images.search.yahoo.com/search/images?"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

