css:
@-moz-document url-prefix("https://edition.francesoir.fr/"), domain("www.francesoir.fr") {

a
{
    text-decoration: none !important;
}
p a
{
    color: #698196 !important;
}

main, .dropdown-menu
{
    background:#242D34 !important;
}

.node.node--type-article.node--view-mode-teaser > .node__content,
.node.node--type-article.node--view-mode-selection > .node__content,
.node.node--type-dossier.node--view-mode-teaser > .node__content,
.node.node--type-dossier.node--view-mode-selection > .node__content
{
    background:#2D3740 !important;
    color: #938686 !important;
    box-shadow: 0 1px 1px 0 #1A2126;
}
.node.node--type-article.node--view-mode-teaser:hover > .node__content,
.node.node--type-article.node--view-mode-selection:hover > .node__content,
.node.node--type-dossier.node--view-mode-teaser:hover > .node__content,
.node.node--type-dossier.node--view-mode-selection:hover > .node__content
{
    background:#2D3740 !important;
    color: #938686 !important;
    box-shadow: 0 2px 1px 0 #698196;
}

.node.node--type-article.node--view-mode-selection .node__content *,
.node.node--type-dossier.node--view-mode-selection .node__content *
{
	color: #808040 !important;
}

.node.node--type-article.node--view-mode-selection .node__content .desc-content .bottom,
.node.node--type-dossier.node--view-mode-selection .node__content .desc-content .bottom
{
	background-color: #242D34 !important;
	border-radius: 2px;
    border: 1px solid #698196  !important;
}
.node.node--type-article.node--view-mode-selection .node__content .desc-content .bottom:hover,
.node.node--type-dossier.node--view-mode-selection .node__content .desc-content .bottom:hover
{
	background-color: #42515E !important;
	border-radius: 2px;
    border: 1px solid #698196  !important;
}

header .navbar
{
    background:#2D3740 !important;
    box-shadow: 0 2px 2px 0 #242D34;    
}
a.nav-item.nav-link
{
    color: #938686 !important;    
}
a.nav-item.nav-link:hover
{
    color: #DD8F00 !important;    
}




/* =============================================== Menus déroulants ================= */
.block-menu.menu--main .dropdown-menu a
{
	color: #698196;
	background-color: #242D34 !important;
}
.block-menu.menu--main .dropdown-menu a:hover
{
	color: #DD8F00;
	background-color: #2D3740 !important;
}

/* =========================================== Articles =============== */

h1
{
    color: #DD8F00 !important;
}

.node a:hover
{
   color: #DD8F00 !important; 
}


.node.node--type-article.node--view-mode-full,
.node.node--type-dossier.node--view-mode-full,
.actu-taxo-fil
{
	background-color: #2D3740;
	border-radius: 10px;
	padding: 15px 0;
    color: #938686 !important;
    box-shadow: 0 1px 1px 0 #1A2126,0 0px 0px 0 #1A2126;
}


/* ================================================ Aside =============== */

.view-header a, .actu-taxo-fil h4
{
    color: #DD8F00 !important;
}
.view-header a:hover
{
    color: #2B95FF !important;
}



.region.region-sidebar-second .block-views
{
	background-color: #2D3740;
    color: #9B8E8E !important;
	box-shadow: 0 1px 1px 0 #1A2126,0 0px 0px 0 #1A2126;
}
.region.region-sidebar-second .block-views .views-row
{
	padding: 1rem 0 0.2rem;
	border-bottom: 1px dashed #9B8E8E;
}
span.field-content a, .actu-taxo-fil a
{
    color: #698196 ;
}
span.field-content a:hover, .actu-taxo-fil a:hover
{
    color: #DD8F00 ;
}




blockquote
{
    background: #242D34 !important;
    color: #788DA0 !important
}
blockquote a
{
    color: #A79A9A !important
}


/* ==================================== SUPPRESSIONS =============== */

.mt-5, footer, .sib-form, #block-soutenir,
.paragraph.paragraph--type--home-block-perso
{
    display: none !important;
}
















}
