css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {

div[class="ad hdr"],
div[class="ad uprrail1"],
div[class="ad uprrail2"],
div[class="side-container newsletter"],
div[class="side-container"],
div[class="ad lwrrail"],
div[class="ad lwrinl"],
div[class="ad-lwrinl-wrapper"],
div[id="footer"],
div[class="comments_ad_image"],
div[class="news-summary v img-summary sponsored"]
{ display:none; }

body { background:#D6DBDE!important; }

.news-body a.body { color:#222!important; }

#container { width:1000px;margin-bottom:10px!important;
background:#fff!important;border-bottom:2px solid #1B5790!important; }

#h { margin-bottom:15px!important; }
#h-pri { background:#8F4788!important;border-top:1px solid #CF81C6; }
#h-pri li.h-drop a, #h-sec li a 
{ color:#fff!important; }
#h-pri li a:hover strong, #h-pri li a:focus strong, 
#h-pri div a:hover strong, #h-pri div a:focus strong
{ background:#CF9BCA!important;color:#2F032B!important;
border:1px solid #EFEFEF;border-width:1px 0 1px 1px;
padding-top:4px!important;padding-bottom:4px!important;padding-left:4px!important; }
#h-pri li.h-drop ul { padding:0!important; margin-left:0px!important; }
#h-pri li.h-drop ul li a { background:#000;color:#2F032B!important; }

#h-sec { background:#538F35!important;border-top:1px solid #97BF83;
border-bottom:1px solid #416F29; }
#h-sec ul { background:none!important; }
#h-sec li a {font-size:90%!important; }
#h li.extra a,#h #h-sec #h-upcoming a 
{ border-color:#ccc!important; }
#h-sec li.current { background:#3C6F22!important; }


div[class="corpus-contents"] { padding-bottom:0!important; }
br[style="clear: both;"] { display:none; }
#contents { padding-bottom:10px!important;margin-bottom:10px!important;
overflow:hidden; }
.corpus-contents { background:none!important;padding:0!important; }
.corpus-wrapper { background:#fff!important;padding-top:10px!important; }

.list .sidebar { top:5px!important; }
.side-header img, .header-login, .side-header a, .header-about 
{ border-color:#fff!important; }
.side-options { position:static!important; }
#capsule_TopInTopics { padding-top:0!important; }

}
