css:
@-moz-document url-prefix(http://books.google.com) {

/* New York Times */
[style="padding: 4px 0pt 4px 8px; font-size: small;"] > [href^="http://topics.nytimes.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* ReviewScout */
[style="padding: 4px 0pt 4px 8px; font-size: small;"] > a + [href^="http://www.reviewscout.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Amazon */
[style="padding: 4px 0pt 4px 8px; font-size: small;"] > a + a + [href^="http://www.amazon.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Barnes and Noble */
[style="padding: 4px 0pt 4px 8px; font-size: small;"] > a + a + a + [href^="http://search.barnesandnoble.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

/* Blackwell */
[style="padding: 4px 0pt 4px 8px; font-size: small;"] > a + a + a + a + [href^="http://bookshop.blackwell.com"]:hover
	{cursor:url("data:image/png;base64,"), pointer;}

[style="padding: 4px 0pt 4px 8px; font-size: small;"]
{padding: 8px 0px 0px 0px !important;} /* top right bottom left */

}
