css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ebaumsworld.com") {


/* Remove crap */
#navContainer, #subSidebar, #footer, #meebo, #watchVideoLeftCol, #watchVideoRightCol, #videoOptions, #watchVideoBottomHalf, #mediaDetailsContainer, #topNotes {
 display:none !important;
}



/* Center video/images */
#watchVideoCenterCol, #videoContentContainer, #videoPlayer, #centerContainer, #contentContainer {
 width: 100% !important;
 background-color: white !important;
}



/* Makes video slightly larger */
/* #flashContainer {
 width: 640px !important;
 height: 480px !important;
}
*/



/* Extends page to bottom */
html, body, #siteContainer {
 height: 100% !important;
 border: 0 !important;
}



/* Remove ugly borders */
#watchVideoMediaInfo {
 border: 0px !important;
}



/* Fix title centering and colors */
#videoTitle, #mediainfo {
 width: 100% !important;
 margin: 10px 0 0 !important;
}
#videoTitle, H1, #mediainfo, #enlarge {
 display: inline-block !important;
 border: 0 !important;
 color: black !important;
 text-align: center !important;
 background-color: white !important;
}

}
