css:
@-moz-document url(https://mastodon.social/home) {
     .columns-area__panels__pane.columns-area__panels__pane--start.columns-area__panels__pane--navigational {
         grid-area: Navigation;
         justify-content: flex-end;
    }
     .columns-area__panels {
         display: grid;
         grid-template-columns: 0.7fr 1fr;
         grid-template-rows: minmax(100px,400px);
         gap: 0px 0px;
         grid-template-areas: "Navigation Compose" "Navigation Feed";
    }
     .columns-area__panels__pane.columns-area__panels__pane--compositional {
         grid-area: Compose;
         width: auto;
         height: auto;
         max-width: 600px;
        /* background: red;
         */
    }
     .columns-area__panels__main {
         grid-area: Feed;
         position: relative;
         width: 100%;
        /* top: 620px;
         */
         max-width: 600px;
    }
     .columns-area__panels__pane__inner {
         width: 100%;
         position: static;
         height: auto;
    }
     .columns-area__panels__pane.columns-area__panels__pane--start.columns-area__panels__pane--navigational .columns-area__panels__pane__inner {
         width: 240px;
    }
     .compose-panel {
         width: 100%;
    }
     .link-footer {
         position: absolute;
         left: 0;
         top: 100%;
         width: 185px;
         right: 502px;
         margin: 0 auto;
    }
     .compose-form__autosuggest-wrapper {
         background: transparent;
    }
     textarea.autosuggest-textarea__textarea {
         background: transparent;
    }
     .compose-form__autosuggest-wrapper {
         background: transparent;
    }
     .compose-form .autosuggest-textarea__textarea, .compose-form .spoiler-input__input {
         background: transparent;
         font-size: 1.3rem;
         color: #fff;
    }
     .compose-panel .compose-form__autosuggest-wrapper {
         background: transparent;
    }
     .compose-form .compose-form__modifiers {
         background: transparent;
    }
     .compose-form .compose-form__buttons-wrapper {
         background: rgba(255,255,255,.04);
    }
     span.display-name {
         display: flex;
         gap: 10px;
    }
}
