css:
@-moz-document domain("www.cnbeta.com"), domain("hot.cnbeta.com") {
.topContent,.cnbeta-side-sponsor,.cnbeta-headlines-title,.swiper-slide ul,.global-navi,.cnbeta-home-side,.cnbeta-update-list-article,.cnbeta-update-list .item .meta-data .labels,.cnbeta-headlines,.cbv{ display:none;}

.cnbeta-update-list{ margin:20px 0 0 0}
.w1200{ width:960px}

.cnbeta-swiper-headlines .mask{ background:#ffffff}
.cb-group-carousel .swiper-slide,.cnbeta-swiper-headlines{ height:265px}
.item{background:#ffffff !important;}
.cnbeta-update-list .item dl img{ position: absolute;left:auto;right:20px !important; border-radius:0;border:none;}
.cnbeta-update-list .item dl{padding:20px 190px 10px 32px;font-family:PingFang SC,Lantinghei SC,Helvetica Neue,Helvetica,Arial,Microsoft YaHei,\\5FAE\8F6F\96C5\9ED1,STHeitiSC-Light,simsun,\\5B8B\4F53,WenQuanYi Zen Hei,WenQuanYi Micro Hei,"sans-serif"}
.cnbeta-update-list .item dt{ font-size:20px;}
.cnbeta-update-list .item dd{font-size:15px; line-height:180%;}
.cnbeta-update-list{ width:100%; float:none;}
.cnbeta-update-list .item dl{ background:transparent;}
.cnbeta-update-list .item .meta-data .status{ right:200px;}
.cnbeta-update-list .item .meta-data .status>.b{ display:none;}

.cnbeta-article-latest,.cnbeta-home-side{display:none;}
.cnbeta-article{ float:none; margin:0 auto;}


}
