css:
@-moz-document domain("2ch.hk") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.post_type_reply:before {
    content: ''!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.fm__header
{
	font-size: 2.5rem!important;
}

body
{
	font-size: 1.85rem!important;
}

.pr-tags-container > a
{
	font-size: 1.52rem!important;
}

span[style="background-color: rgb(247, 247, 247); color: rgb(255, 0, 0);"]
{
	line-height: 2.5rem!important;
}

p  {
    line-height: 2.3;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.header__logo,
.mmm
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.reply
 {
    width: 17rem!important;
    height: 8rem!important;
    margin: 15px 25px!important;
	padding: 12px!important;
    font-size: 65%!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.post__image,
.post_type_reply,
.thread
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

span[style="background-color: rgb(247, 247, 247); color: rgb(255, 0, 0);"],
span[style="background-color: rgb(255, 0, 0);"]
{
	padding: 13px 26px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

.reply img {
    height: 80px!important;
    float: none!important;
    vertical-align: top;
}

/* Widths 
_____________________________________________________________________________________________________*/

.pr-tags-table,
.post_type_oppost
{
	max-width: none!important;
}

.pr-tags-title {
    width: 12rem!important;
}

.cntnt__aside {
    flex: 0 0 auto!important;
    -webkit-flex: 0 0 auto!important;
}
}

@-moz-document domain("2ch.hk") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	

	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("2ch.hk") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.post__details,
.post__file-attr,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#title,

.fm__header,
.post__title,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

[id*='shrinked-post'] b,
h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.mv__main:after {
    color: #cccccca8;
}


}

@-moz-document domain("2ch.hk") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.warningcover {
    background-color: #090909!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.bb
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.fm__header,
.na,

span[style="background-color: rgb(247, 247, 247); color: rgb(255, 0, 0);"]
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.reply
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.reply::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.reply:hover::before
 {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.pr-tags-container > a
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.pr-tags-container > a::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.pr-tags-container > a:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.mv__main,
.post__image,
.post_type_reply
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: 1.5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.fm__item,
.post__images.post__images_type_single .post__image,
.post__images_type_multi .post__image,
.thread
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: 1.5rem!important;
}

.fm__item:hover,
.mv__main:hover,
.post__image:hover,
.post_type_reply:hover,
.thread:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

span[style="background-color: rgb(255, 0, 0);"]
{
	background: linear-gradient(to bottom, #590505 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

span[style="background-color: rgb(255, 0, 0);"]
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}


}

@-moz-document domain("2ch.hk") {
/* Image Settings */

.fm__item,
.post__image,
.reply,
.thread
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.fm__item:hover,
.post__image:hover,
.reply:hover,
.thread:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

selector:hover
 {
	opacity: 1!important;
}

}

@-moz-document domain("2ch.hk") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Arrows _______________________________________________________________________*/

.mv__nav_r,
.na_type_down
{
    transform: none!important;
}

/* Up _________________*/

.na_type_up svg
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

.na_type_down svg
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

selector
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.mv__nav_r svg
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.mv__nav_r svg,
.na_type_down svg,
.na_type_up svg
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}
/* Download ______________________________________________________________________________*/

.icon.js-post-saveimg
{
	background: url(https://i.ibb.co/DrhQ11V/download3.png) no-repeat!important;
	box-sizing: border-box;
}

.icon.js-post-saveimg
{
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.icon.js-post-findimg
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.icon.js-post-findimg
{
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 1px!important;
}


}

@-moz-document domain("2ch.hk") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
