css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* GENERAL */
scrollbar {
-moz-appearance: none !important;
background: none !important;
border: none !important;
min-width: 15px !important;
}
scrollbar[orient="vertical"] {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAABCAYAAAAB3PQ6AAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAG2YAABzjgAA+fYAAIXAAABspQAA7aoAADB4AAAYsTN0GUwAAAA7SURBVHjaBMGBDQBBBADBvRD0X5SWEIn/mZeZn6piZrg7EUFE4O6YGSICwN2xu8wM3U1V8QMAAP//AwB7uBKJmFBrOQAAAABJRU5ErkJggg==")
repeat-y !important;
/* background: -moz-linear-gradient(left, #c6c6c6, #f7f7f7, #d8d8d8) !important; */
}
scrollbar[orient="horizontal"] {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAPCAYAAAAlH6X5AAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAG2YAABzjgAA+fYAAIXAAABspQAA7aoAADB4AAAYsTN0GUwAAABCSURBVHjaJMQxCgAxCEXBxycY738o75ImXVCwcIudYhQRo7UWMjO090bu/se9d2RmkJkjAN57o+6Gc86oqlBm8g0ABksb4Jr2E00AAAAASUVORK5CYII=")
repeat-x !important;
}

/* SCROLLBAR THUMB */
scrollbar[orient="vertical"] thumb *,
scrollbar[orient="horizontal"] thumb *,
scrollbar[orient="vertical"] thumb,
scrollbar[orient="horizontal"] thumb {
-moz-appearance: none !important;
-moz-border-radius: 10px !important;
position: relative !important;
z-index: 1 !important;
background: none !important;
border: solid 1px #60718b !important;
}
scrollbar[orient="vertical"] thumb {
background: -moz-linear-gradient(left, #acb8ca, #7585a3) !important;
}
scrollbar[orient="horizontal"] thumb {
background: -moz-linear-gradient(top, #acb8ca, #7585a3) !important;
}

/* SCROLLBAR ENDS */
scrollbar scrollbarbutton {
-moz-appearance: none !important;
background: url("data:image/png;base64,")
no-repeat !important;
}
scrollbar[orient="vertical"] scrollbarbutton {
min-width: 15px !important;
min-height: 22px !important;
}

scrollbar[orient="horizontal"] scrollbarbutton {
min-width: 22px !important;
min-height: 15px !important;
}

scrollbar scrollbarbutton:hover:active {
background: url("data:image/png;base64,")
no-repeat !important;
}

/* VERTICAL ENDS */

/* UP ARROW */
scrollbar[orient="vertical"] scrollbarbutton[type="decrement"] {
margin: 0px 0px -8px 0px !important;
}
scrollbar[orient="vertical"] scrollbarbutton[type="decrement"]:hover:active {
margin: 0px 0px -8px 0px !important;
}
/* DOWN ARROW */
scrollbar[orient="vertical"] scrollbarbutton[type="increment"] {
background-position: -15px 0px !important;
margin: -8px 0px 0px 0px !important;
}
/* LEFT ARROW */
scrollbar[orient="horizontal"] scrollbarbutton[type="decrement"] {
background-position: 0px -22px !important;
margin: 0px -8px 0px 0px !important;
}
scrollbar[orient="horizontal"] scrollbarbutton[type="decrement"]:hover:active {
margin-right: -8px !important;
}
/* RIGHT ARROW */
scrollbar[orient="horizontal"] scrollbarbutton[type="increment"] {
background-position: -22px -22px !important;
margin: 0px 0px 0px -8px !important;
}
scrollbar[orient="horizontal"] scrollbarbutton[type="increment"]:hover:active {
margin: 0px 0px 0px -8px !important;
}
